detaileddescription.para:
Recently added ChartSeries object.

Examples


Shows how to create an appropriate type of chart series for a graph type. voidChartSeriesCollection_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Thereareseveralwaysofpopulatingachart'sseriescollection.
//Differentseriesschemasareintendedfordifferentcharttypes.
//1-ColumnchartwithcolumnsgroupedandbandedalongtheX-axisbycategory:
SharedPtr<Chart>chart=AppendChart(builder,ChartType::Column,500,300);

ArrayPtr<String>categories=MakeArray<String>({u"Category1",u"Category2",u"Category3"});

//Inserttwoseriesofdecimalvaluescontainingavalueforeachrespectivecategory.
//Thiscolumnchartwillhavethreegroups,eachwithtwocolumns.
chart->get_Series()->Add(u"Series1",categories,MakeArray<double>({76.6,82.1,91.6}));
chart->get_Series()->Add(u"Series2",categories,MakeArray<double>({64.2,79.5,94.0}));

//CategoriesaredistributedalongtheX-axis,andvaluesaredistributedalongtheY-axis.
ASSERT_EQ(ChartAxisType::Category,chart->get_AxisX()->get_Type());
ASSERT_EQ(ChartAxisType::Value,chart->get_AxisY()->get_Type());

//2-AreachartwithdatesdistributedalongtheX-axis:
chart=AppendChart(builder,ChartType::Area,500,300);

ArrayPtr<System::DateTime>dates=
MakeArray<System::DateTime>({System::DateTime(2014,3,31),System::DateTime(2017,1,23),System::DateTime(2017,6,18),
System::DateTime(2019,11,22),System::DateTime(2020,9,7)});

//Insertaserieswithadecimalvalueforeachrespectivedate.
//ThedateswillbedistributedalongalinearX-axis,
//andthevaluesaddedtothisserieswillcreatedatapoints.
chart->get_Series()->Add(u"Series1",dates,MakeArray<double>({15.8,21.5,22.9,28.7,33.1}));

ASSERT_EQ(ChartAxisType::Category,chart->get_AxisX()->get_Type());
ASSERT_EQ(ChartAxisType::Value,chart->get_AxisY()->get_Type());

//3-2Dscatterplot:
chart=AppendChart(builder,ChartType::Scatter,500,300);

//Eachserieswillneedtwodecimalarraysofequallength.
//ThefirstarraycontainsX-values,andthesecondcontainscorrespondingY-values
//ofdatapointsonthechart'sgraph.
chart->get_Series()->Add(u"Series1",MakeArray<double>({3.1,3.5,6.3,4.1,2.2,8.3,1.2,3.6}),
MakeArray<double>({3.1,6.3,4.6,0.9,8.5,4.2,2.3,9.9}));
chart->get_Series()->Add(u"Series2",MakeArray<double>({2.6,7.3,4.5,6.6,2.1,9.3,0.7,3.3}),
MakeArray<double>({7.1,6.6,3.5,7.8,7.7,9.5,1.3,4.6}));

ASSERT_EQ(ChartAxisType::Value,chart->get_AxisX()->get_Type());
ASSERT_EQ(ChartAxisType::Value,chart->get_AxisY()->get_Type());

//4-Bubblechart:
chart=AppendChart(builder,ChartType::Bubble,500,300);

//Eachserieswillneedthreedecimalarraysofequallength.
//ThefirstarraycontainsX-values,thesecondcontainscorrespondingY-values,
//andthethirdcontainsdiametersforeachofthegraph'sdatapoints.
chart->get_Series()->Add(u"Series1",MakeArray<double>({1.1,5.0,9.8}),MakeArray<double>({1.2,4.9,9.9}),MakeArray<double>({2.0,4.0,8.0}));

doc->Save(ArtifactsDir+u"Charts.ChartSeriesCollection.docx");
}

staticSharedPtr<Chart>AppendChart(SharedPtr<DocumentBuilder>builder,ChartTypechartType,doublewidth,doubleheight)
{
SharedPtr<Shape>chartShape=builder->InsertChart(chartType,width,height);
SharedPtr<Chart>chart=chartShape->get_Chart();
chart->get_Series()->Clear();

returnchart;
}

