Examples


Shows how to add and remove series data in a chart. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertacolumnchartthatwillcontainthreeseriesofdemodatabydefault.
SharedPtr<Shape>chartShape=builder->InsertChart(ChartType::Column,400,300);
SharedPtr<Chart>chart=chartShape->get_Chart();

//Eachserieshasfourdecimalvalues:oneforeachofthefourcategories.
//Fourclustersofthreecolumnswillrepresentthisdata.
SharedPtr<ChartSeriesCollection>chartData=chart->get_Series();

ASSERT_EQ(3,chartData->get_Count());

//Printthenameofeveryseriesinthechart.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<ChartSeries>>>enumerator=chart->get_Series()->GetEnumerator();
while(enumerator->MoveNext())
{
std::cout<<enumerator->get_Current()->get_Name()<<std::endl;
}
}

//Thesearethenamesofthecategoriesinthechart.
ArrayPtr<String>categories=MakeArray<String>({u"Category1",u"Category2",u"Category3",u"Category4"});

//Wecanaddaserieswithnewvaluesforexistingcategories.
//Thischartwillnowcontainfourclustersoffourcolumns.
chart->get_Series()->Add(u"Series4",categories,MakeArray<double>({4.4,7.0,3.5,2.1}));

//Achartseriescanalsoberemovedbyindex,likethis.
//Thiswillremoveoneofthethreedemoseriesthatcamewiththechart.
chartData->RemoveAt(2);

ASSERT_FALSE(chartData->LINQ_Any([](SharedPtr<ChartSeries>s){returns->get_Name()==u"Series3";}));

//Wecanalsoclearallthechart'sdataatoncewiththismethod.
//Whencreatinganewchart,thisisthewaytowipeallthedemodata
//beforewecanbeginworkingonablankchart.
chartData->Clear();

