//CheckFillpropertiesoftheFont.
std::cout<<String::Format(u"Thetypeofthefillis:{0}",fill->get_FillType())<<std::endl;
std::cout<<"Theforegroundcolorofthefillis:"<<fill->get_ForeColor()<<std::endl;
std::cout<<"Thefillistransparentat"<<(fill->get_Transparency()*100)<<"%"<<std::endl;

//ChangetypeofthefilltoSolidwithuniformgreencolor.
fill->Solid(System::Drawing::Color::get_Green());
std::cout<<"\nThefillischanged:"<<std::endl;
std::cout<<String::Format(u"Thetypeofthefillis:{0}",fill->get_FillType())<<std::endl;
std::cout<<"Theforegroundcolorofthefillis:"<<fill->get_ForeColor()<<std::endl;
std::cout<<"Thefilltransparencyis"<<(fill->get_Transparency()*100)<<"%"<<std::endl;

doc->Save(ArtifactsDir+u"Drawing.FillSolid.docx");

