//CheckFillpropertiesoftheFont.
std::cout<<String::Format(u"Thetypeofthefillis:{0}",fill->get_FillType())<<std::endl;
std::cout<<"Theforegroundcolorofthefillis:"<<fill->get_ForeColor()<<std::endl;
