Examples


Shows how to fill a shape with a solid color. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Writesometext,andthencoveritwithafloatingshape.
builder->get_Font()->set_Size(32);
builder->Writeln(u"Helloworld!");

SharedPtr<Shape>shape=builder->InsertShape(ShapeType::CloudCallout,RelativeHorizontalPosition::LeftMargin,25,RelativeVerticalPosition::TopMargin,
25,250,150,WrapType::None);

//Usethe"StrokeColor"propertytosetthecoloroftheoutlineoftheshape.
shape->set_StrokeColor(System::Drawing::Color::get_CadetBlue());

//Usethe"FillColor"propertytosetthecoloroftheinsideareaoftheshape.
shape->set_FillColor(System::Drawing::Color::get_LightBlue());

//The"Opacity"propertydetermineshowtransparentthecolorisona0-1scale,
//with1beingfullyopaque,and0beinginvisible.
//Theshapefillbydefaultisfullyopaque,sowecannotseethetextthatthisshapeisontopof.
ASPOSE_ASSERT_EQ(1.0,shape->get_Fill()->get_Opacity());

//Settheshapefillcolor'sopacitytoalowervaluesothatwecanseethetextunderneathit.
shape->get_Fill()->set_Opacity(0.3);

doc->Save(ArtifactsDir+u"Shape.Fill.docx");
