Examples


Shows how to extract embedded OLE objects into files. autodoc=MakeObject<Document>(MyDir+u"OLEspreadsheet.docm");
autoshape=System::DynamicCast<Shape>(doc->GetChild(NodeType::Shape,0,true));

//TheOLEobjectinthefirstshapeisaMicrosoftExcelspreadsheet.
SharedPtr<OleFormat>oleFormat=shape->get_OleFormat();

ASSERT_EQ(u"Excel.Sheet.12",oleFormat->get_ProgId());

//Ourobjectisneitherautoupdatingnorlockedfromupdates.
ASSERT_FALSE(oleFormat->get_AutoUpdate());
ASPOSE_ASSERT_EQ(false,oleFormat->get_IsLocked());

//IfweplanonsavingtheOLEobjecttoafileinthelocalfilesystem,
//wecanusethe"SuggestedExtension"propertytodeterminewhichfileextensiontoapplytothefile.
ASSERT_EQ(u".xlsx",oleFormat->get_SuggestedExtension());

//BelowaretwowaysofsavinganOLEobjecttoafileinthelocalfilesystem.
//1-Saveitviaastream:
{
autofs=MakeObject<System::IO::FileStream>(ArtifactsDir+u"OLEspreadsheetextractedviastream"+oleFormat->get_SuggestedExtension(),
System::IO::FileMode::Create);
oleFormat->Save(fs);
}

//2-Saveitdirectlytoafilename:
oleFormat->Save(ArtifactsDir+u"OLEspreadsheetsaveddirectly"+oleFormat->get_SuggestedExtension());

Examples


Shows how to access an OLE control embedded in a document and its child controls. autodoc=MakeObject<Document>(MyDir+u"OLEActiveXcontrols.docm");

//ShapesstoreanddisplayOLEobjectsinthedocument'sbody.
autoshape=System::DynamicCast<Shape>(doc->GetChild(NodeType::Shape,0,true));

ASSERT_EQ(u"6e182020-f460-11ce-9bcd-00aa00608e01",System::ObjectExt::ToString(shape->get_OleFormat()->get_Clsid()));

autooleControl=System::DynamicCast<Forms2OleControl>(shape->get_OleFormat()->get_OleControl());

//SomeOLEcontrolsmaycontainchildcontrols,suchastheoneinthisdocumentwiththreeoptionsbuttons.
SharedPtr<Forms2OleControlCollection>oleControlCollection=oleControl->get_ChildNodes();

ASSERT_EQ(3,oleControlCollection->get_Count());

ASSERT_EQ(u"C#",oleControlCollection->idx_get(0)->get_Caption());
ASSERT_EQ(u"1",oleControlCollection->idx_get(0)->get_Value());

ASSERT_EQ(u"VisualBasic",oleControlCollection->idx_get(1)->get_Caption());
ASSERT_EQ(u"0",oleControlCollection->idx_get(1)->get_Value());

ASSERT_EQ(u"Delphi",oleControlCollection->idx_get(2)->get_Caption());
ASSERT_EQ(u"0",oleControlCollection->idx_get(2)->get_Value());

Examples


Shows how to insert linked and unlinked OLE objects. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//EmbedaMicrosoftVisiodrawingintothedocumentasanOLEobject.
builder->InsertOleObject(ImageDir+u"MicrosoftVisiodrawing.vsd",u"Package",false,false,nullptr);

//Insertalinktothefileinthelocalfilesystemanddisplayitasanicon.
builder->InsertOleObject(ImageDir+u"MicrosoftVisiodrawing.vsd",u"Package",true,true,nullptr);

//InsertingOLEobjectscreatesshapesthatstoretheseobjects.
ArrayPtr<SharedPtr<Shape>>shapes=doc->GetChildNodes(NodeType::Shape,true)->LINQ_OfType<SharedPtr<Shape>>()->LINQ_ToArray();

ASSERT_EQ(2,shapes->get_Length());
autoisOleObject=[](SharedPtr<Shape>s)
{
returns->get_ShapeType()==ShapeType::OleObject;
};
ASSERT_EQ(2,shapes->LINQ_Count(isOleObject));

//IfashapecontainsanOLEobject,itwillhaveavalid"OleFormat"property,
//whichwecanusetoverifysomeaspectsoftheshape.
SharedPtr<OleFormat>oleFormat=shapes[0]->get_OleFormat();

ASPOSE_ASSERT_EQ(false,oleFormat->get_IsLink());
ASPOSE_ASSERT_EQ(false,oleFormat->get_OleIcon());

oleFormat=shapes[1]->get_OleFormat();

ASPOSE_ASSERT_EQ(true,oleFormat->get_IsLink());
ASPOSE_ASSERT_EQ(true,oleFormat->get_OleIcon());

ASSERT_TRUE(oleFormat->get_SourceFullName().EndsWith(String(u"Images")+System::IO::Path::DirectorySeparatorChar+u"MicrosoftVisiodrawing.vsd"));
ASSERT_EQ(u"",oleFormat->get_SourceItem());

ASSERT_EQ(u"MicrosoftVisiodrawing.vsd",oleFormat->get_IconCaption());

doc->Save(ArtifactsDir+u"Shape.OleLinks.docx");

//IftheobjectcontainsOLEdata,wecanaccessitusingastream.
{
SharedPtr<System::IO::MemoryStream>stream=oleFormat->GetOleEntry(u"\x0001"
u"CompObj");
ArrayPtr<uint8_t>oleEntryBytes=stream->ToArray();
ASSERT_EQ(76,oleEntryBytes->get_Length());
}

