ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(50.0f,50.0f,100.0f,100.0f),line->get_BoundsInPoints());

//Createagroupshape,andthensetthesizeofitscontainingblockusingthe"Bounds"property.
autogroup=MakeObject<GroupShape>(doc);
group->set_Bounds(System::Drawing::RectangleF(0.0f,100.0f,250.0f,250.0f));

ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(0.0f,100.0f,250.0f,250.0f),group->get_BoundsInPoints());

//Createarectangle,verifythesizeofitsboundingblock,andthenaddittothegroupshape.
autorectangle=MakeObject<Shape>(doc,ShapeType::Rectangle);
rectangle->set_Width(100);
rectangle->set_Height(100);
rectangle->set_Left(700);
rectangle->set_Top(700);

ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(700.0f,700.0f,100.0f,100.0f),rectangle->get_BoundsInPoints());

group->AppendChild(rectangle);

//Thegroupshape'scoordinateplanehasitsoriginonthetopleft-handsidecornerofitscontainingblock,
//andthexandycoordinatesof(1000,1000)onthebottomright-handsidecorner.
//Ourgroupshapeis250x250ptinsize,soevery4ptonthegroupshape'scoordinateplane
//translatesto1ptinthedocumentbody'scoordinateplane.
//Everyshapethatweinsertwillalsoshrinkinsizebyafactorof4.
//Thechangeintheshape's"BoundsInPoints"propertywillreflectthis.
ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(175.0f,275.0f,25.0f,25.0f),rectangle->get_BoundsInPoints());

doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(group);

//Insertashapeandplaceitoutsideoftheboundsofthegroupshape'scontainingblock.
autoshape=MakeObject<Shape>(doc,ShapeType::Rectangle);
shape->set_Width(100);
shape->set_Height(100);
shape->set_Left(1000);
shape->set_Top(1000);

group->AppendChild(shape);

//Thegroupshape'sfootprintinthedocumentbodyhasincreased,butthecontainingblockremainsthesame.
ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(0.0f,100.0f,250.0f,250.0f),group->get_BoundsInPoints());
ASPOSE_ASSERT_EQ(System::Drawing::RectangleF(250.0f,350.0f,25.0f,25.0f),shape->get_BoundsInPoints());

doc->Save(ArtifactsDir+u"Shape.Bounds.docx");

