image->set_Top(-225);
group->AppendChild(image);

(System::DynamicCast<Shape>(group->GetChild(NodeType::Shape,3,true)))->get_ImageData()->SetImage(ImageDir+u"Logo.jpg");

//Insertatextboxintothegroupshape.Setthe"Left"propertysothatthetextbox'srightedge
//touchestherightboundaryofthegroupshape.Setthe"Top"propertysothatthetextboxsitsoutside
//theboundaryofthegroupshape,withitstopsizelinedupalongthegroupshape'sbottommargin.
autotextBox=MakeObject<Shape>(doc,ShapeType::TextBox);
textBox->set_Width(200);
textBox->set_Height(50);
textBox->set_Left(group->get_CoordSize().get_Width()+group->get_CoordOrigin().get_X()-200);
textBox->set_Top(group->get_CoordSize().get_Height()+group->get_CoordOrigin().get_Y());
group->AppendChild(textBox);

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->InsertNode(group);
builder->MoveTo((System::DynamicCast<Shape>(group->GetChild(NodeType::Shape,4,true)))->AppendChild(MakeObject<Paragraph>(doc)));
builder->Write(u"Helloworld!");

doc->Save(ArtifactsDir+u"Shape.GroupShape.docx");

Shows how to translate the x and y coordinate location on a shape's coordinate plane to a location on the parent shape's coordinate plane. autodoc=MakeObject<Document>();

//Insertagroupshape,andplaceit100pointsbelowandtotherightof
//thedocument'sxandYcoordinateoriginpoint.
autogroup=MakeObject<GroupShape>(doc);
group->set_Bounds(System::Drawing::RectangleF(100.0f,100.0f,500.0f,500.0f));

//Usethe"LocalToParent"methodtodeterminethat(0,0)onthegroup'sinternalxandycoordinates
//lieson(100,100)ofitsparentshape'scoordinatesystem.Thegroupshape'sparentisthedocumentitself.
ASPOSE_ASSERT_EQ(System::Drawing::PointF(100.0f,100.0f),group->LocalToParent(System::Drawing::PointF(0.0f,0.0f)));

//Bydefault,ashape'sinternalcoordinateplanehasthetopleftcornerat(0,0),
//andthebottomrightcornerat(1000,1000).Duetoitssize,ourgroupshapecoversanareaof500ptx500pt
//inthedocument'splane.Thismeansthatamovementof1ptonthedocument'scoordinateplanewilltranslate
//toamovementof2ptsonthegroupshape'scoordinateplane.
ASPOSE_ASSERT_EQ(System::Drawing::PointF(150.0f,150.0f),group->LocalToParent(System::Drawing::PointF(100.0f,100.0f)));
ASPOSE_ASSERT_EQ(System::Drawing::PointF(200.0f,200.0f),group->LocalToParent(System::Drawing::PointF(200.0f,200.0f)));
ASPOSE_ASSERT_EQ(System::Drawing::PointF(250.0f,250.0f),group->LocalToParent(System::Drawing::PointF(300.0f,300.0f)));

//Movethegroupshape'sxandyaxisoriginfromthetopleftcornertothecenter.
//Thiswilloffsetthegroup'sinternalcoordinatesrelativetothedocument'scoordinatesevenfurther.
group->set_CoordOrigin(System::Drawing::Point(-250,-250));

ASPOSE_ASSERT_EQ(System::Drawing::PointF(375.0f,375.0f),group->LocalToParent(System::Drawing::PointF(300.0f,300.0f)));

//Changingthescaleofthecoordinateplanewillalsoaffectrelativelocations.
group->set_CoordSize(System::Drawing::Size(500,500));

ASPOSE_ASSERT_EQ(System::Drawing::PointF(650.0f,650.0f),group->LocalToParent(System::Drawing::PointF(300.0f,300.0f)));

//Ifwewishtoaddashapetothisgroupwhiledefiningitslocationbasedonalocationinthedocument,
//wewillneedtofirstconfirmalocationinthegroupshapethatwillmatchthedocument'slocation.
ASPOSE_ASSERT_EQ(System::Drawing::PointF(700.0f,700.0f),group->LocalToParent(System::Drawing::PointF(350.0f,350.0f)));

autoshape=MakeObject<Shape>(doc,ShapeType::Rectangle);
shape->set_Width(100);
shape->set_Height(100);
shape->set_Left(700);
shape->set_Top(700);

group->AppendChild(shape);
doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(group);

doc->Save(ArtifactsDir+u"Shape.LocalToParent.docx");

Examples


Shows how to create and populate a group shape. autodoc=MakeObject<Document>();

//Createagroupshape.Agroupshapecandisplayacollectionofchildshapenodes.
//InMicrosoftWord,clickingwithinthegroupshape'sboundaryorononeofthegroupshape'schildshapeswill
//selectalltheotherchildshapeswithinthisgroupandallowustoscaleandmovealltheshapesatonce.
autogroup=MakeObject<GroupShape>(doc);

ASSERT_EQ(WrapType::None,group->get_WrapType());

//Createa400ptx400ptgroupshapeandplaceitatthedocument'sfloatingshapecoordinateorigin.
group->set_Bounds(System::Drawing::RectangleF(0.0f,0.0f,400.0f,400.0f));

//Setthegroup'sinternalcoordinateplanesizeto500x500pt.
//Thetopleftcornerofthegroupwillhaveanxandycoordinateof(0,0),
//andthebottomrightcornerwillhaveanxandycoordinateof(500,500).
group->set_CoordSize(System::Drawing::Size(500,500));

//Setthecoordinatesofthetopleftcornerofthegroupto(-250,-250).
//Thegroup'scenterwillnowhaveanxandycoordinatevalueof(0,0),
//andthebottomrightcornerwillbeat(250,250).
group->set_CoordOrigin(System::Drawing::Point(-250,-250));

//Createarectanglethatwilldisplaytheboundaryofthisgroupshapeandaddittothegroup.
autorectangle=MakeObject<Shape>(doc,ShapeType::Rectangle);
rectangle->set_Width(group->get_CoordSize().get_Width());
rectangle->set_Height(group->get_CoordSize().get_Height());
rectangle->set_Left(group->get_CoordOrigin().get_X());
rectangle->set_Top(group->get_CoordOrigin().get_Y());
group->AppendChild(rectangle);

//Onceashapeisapartofagroupshape,wecanaccessitasachildnodeandthenmodifyit.
(System::DynamicCast<Shape>(group->GetChild(NodeType::Shape,0,true)))->get_Stroke()->set_DashStyle(DashStyle::Dash);

//Createasmallredstarandinsertitintothegroup.
//Lineuptheshapewiththegroup'scoordinateorigin,whichwehavemovedtothecenter.
autosmallRedStar=MakeObject<Shape>(doc,ShapeType::Star);
smallRedStar->set_Width(20);
smallRedStar->set_Height(20);
smallRedStar->set_Left(-10);
smallRedStar->set_Top(-10);
smallRedStar->set_FillColor(System::Drawing::Color::get_Red());
group->AppendChild(smallRedStar);

//Insertarectangle,andtheninsertaslightlysmallerrectangleinthesameplacewithanimage.
//Newershapesthatweaddtothegroupoverlapoldershapes.Thelightbluerectanglewillpartiallyoverlaptheredstar,
//andthentheshapewiththeimagewilloverlapthelightbluerectangle,usingitasaframe.
//Wecannotusethe"ZOrder"propertiesofshapestomanipulatetheirarrangementwithinagroupshape.
autolightBlueRectangle=MakeObject<Shape>(doc,ShapeType::Rectangle);
lightBlueRectangle->set_Width(250);
lightBlueRectangle->set_Height(250);
lightBlueRectangle->set_Left(-250);
lightBlueRectangle->set_Top(-250);
lightBlueRectangle->set_FillColor(System::Drawing::Color::get_LightBlue());
group->AppendChild(lightBlueRectangle);

autoimage=MakeObject<Shape>(doc,ShapeType::Image);
image->set_Width(200);
image->set_Height(200);
image->set_Left(-225);
image->set_Top(-225);
group->AppendChild(image);

(System::DynamicCast<Shape>(group->GetChild(NodeType::Shape,3,true)))->get_ImageData()->SetImage(ImageDir+u"Logo.jpg");

//Insertatextboxintothegroupshape.Setthe"Left"propertysothatthetextbox'srightedge
//touchestherightboundaryofthegroupshape.Setthe"Top"propertysothatthetextboxsitsoutside
//theboundaryofthegroupshape,withitstopsizelinedupalongthegroupshape'sbottommargin.
autotextBox=MakeObject<Shape>(doc,ShapeType::TextBox);
textBox->set_Width(200);
textBox->set_Height(50);
textBox->set_Left(group->get_CoordSize().get_Width()+group->get_CoordOrigin().get_X()-200);
textBox->set_Top(group->get_CoordSize().get_Height()+group->get_CoordOrigin().get_Y());
group->AppendChild(textBox);

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->InsertNode(group);
builder->MoveTo((System::DynamicCast<Shape>(group->GetChild(NodeType::Shape,4,true)))->AppendChild(MakeObject<Paragraph>(doc)));
builder->Write(u"Helloworld!");

doc->Save(ArtifactsDir+u"Shape.GroupShape.docx");

