Shows how to set the wrapping distance for a text that surrounds a shape. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertarectangleand,getthetexttowraptightlyarounditsbounds.
SharedPtr<Shape>shape=builder->InsertShape(ShapeType::Rectangle,150,150);
shape->set_WrapType(WrapType::Tight);

//Settheminimumdistancebetweentheshapeandsurroundingtextto40ptfromallsides.
shape->set_DistanceTop(40);
shape->set_DistanceBottom(40);
shape->set_DistanceLeft(40);
shape->set_DistanceRight(40);

//Movetheshapeclosertothecenterofthepage,andthenrotatetheshape60degreesclockwise.
shape->set_Top(75);
shape->set_Left(150);
shape->set_Rotation(60);

//Addtextthatwillwraparoundtheshape.
builder->get_Font()->set_Size(24);
builder->Write(String(u"Loremipsumdolorsitamet,consecteturadipiscingelit,seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.")+
u"Utenimadminimveniam,quisnostrudexercitationullamcolaborisnisiutaliquipexeacommodoconsequat.");

doc->Save(ArtifactsDir+u"Shape.Coordinates.docx");

