Shows how to determine whether a shape is inline or floating. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Belowaretwowrappingtypesthatshapesmayhave.
//1-Inline:
builder->Write(u"Helloworld!");
SharedPtr<Shape>shape=builder->InsertShape(ShapeType::Rectangle,100,100);
shape->set_FillColor(System::Drawing::Color::get_LightBlue());
builder->Write(u"Helloagain.");

//Aninlineshapesitsinsideaparagraphamongotherparagraphelements,suchasrunsoftext.
//InMicrosoftWord,wemayclickanddragtheshapetoanyparagraphasifitisacharacter.
//Iftheshapeislarge,itwillaffectverticalparagraphspacing.
//Wecannotmovethisshapetoaplacewithnoparagraph.
ASSERT_EQ(WrapType::Inline,shape->get_WrapType());
ASSERT_TRUE(shape->get_IsInline());

//2-Floating:
shape=builder->InsertShape(ShapeType::Rectangle,RelativeHorizontalPosition::LeftMargin,200,RelativeVerticalPosition::TopMargin,200,100,100,
WrapType::None);
shape->set_FillColor(System::Drawing::Color::get_Orange());

//Afloatingshapebelongstotheparagraphthatweinsertitinto,
//whichwecandeterminebyananchorsymbolthatappearswhenweclicktheshape.
//Iftheshapedoesnothaveavisibleanchorsymboltoitsleft,
//wewillneedtoenablevisibleanchorsvia"Options"->"Display"->"ObjectAnchors".
//InMicrosoftWord,wemayleftclickanddragthisshapefreelytoanylocation.
ASSERT_EQ(WrapType::None,shape->get_WrapType());
ASSERT_FALSE(shape->get_IsInline());

doc->Save(ArtifactsDir+u"Shape.IsInline.docx");

