Shows how to determine how to display a shape in a table cell. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->InsertCell();
builder->EndTable();

autotableStyle=System::DynamicCast<TableStyle>(doc->get_Styles()->Add(StyleType::Table,u"MyTableStyle1"));
tableStyle->set_BottomPadding(20);
tableStyle->set_LeftPadding(10);
tableStyle->set_RightPadding(10);
tableStyle->set_TopPadding(20);
tableStyle->get_Borders()->set_Color(System::Drawing::Color::get_Black());
tableStyle->get_Borders()->set_LineStyle(LineStyle::Single);

table->set_Style(tableStyle);

builder->MoveTo(table->get_FirstRow()->get_FirstCell()->get_FirstParagraph());

SharedPtr<Shape>shape=builder->InsertShape(ShapeType::Rectangle,RelativeHorizontalPosition::LeftMargin,50,RelativeVerticalPosition::TopMargin,
100,100,100,WrapType::None);

//Setthe"IsLayoutInCell"propertyto"true"todisplaytheshapeasaninlineelementinsidethecell'sparagraph.
//Thecoordinateoriginthatwilldeterminetheshape'slocationwillbethetopleftcorneroftheshape'scell.
//Ifwere-sizethecell,theshapewillmovetomaintainthesamepositionstartingfromthecell'stopleft.
//Setthe"IsLayoutInCell"propertyto"false"todisplaytheshapeasanindependentfloatingshape.
//Thecoordinateoriginthatwilldeterminetheshape'slocationwillbethetopleftcornerofthepage,
//andtheshapewillnotrespondtoanyre-sizingofitscell.
shape->set_IsLayoutInCell(isLayoutInCell);

//Wecanonlyapplythe"IsLayoutInCell"propertytofloatingshapes.
shape->set_WrapType(WrapType::None);

doc->Save(ArtifactsDir+u"Shape.LayoutInTableCell.docx");

Examples


Shows how to identify move revision shapes. //Amoverevisioniswhenwemoveanelementinthedocumentbodybycut-and-pastingitinMicrosoftWordwhile
//trackingchanges.Ifweinvolveaninlineshapeinsuchatextmovement,thatshapewillalsobearevision.
//Copying-and-pastingormovingfloatingshapesdonotcreatemoverevisions.
autodoc=MakeObject<Document>(MyDir+u"Revisionshape.docx");

//Moverevisionsconsistofpairsof"Movefrom",and"Moveto"revisions.Wemovedinthisdocumentinoneshape,
//butuntilweacceptorrejectthemoverevision,therewillbetwoinstancesofthatshape.
ArrayPtr<SharedPtr<Shape>>shapes=doc->GetChildNodes(NodeType::Shape,true)->LINQ_OfType<SharedPtr<Shape>>()->LINQ_ToArray();

ASSERT_EQ(2,shapes->get_Length());

//Thisisthe"Moveto"revision,whichistheshapeatitsarrivaldestination.
//Ifweaccepttherevision,this"Moveto"revisionshapewilldisappear,
//andthe"Movefrom"revisionshapewillremain.
ASSERT_FALSE(shapes[0]->get_IsMoveFromRevision());
ASSERT_TRUE(shapes[0]->get_IsMoveToRevision());

//Thisisthe"Movefrom"revision,whichistheshapeatitsoriginallocation.
//Ifweaccepttherevision,this"Movefrom"revisionshapewilldisappear,
//andthe"Moveto"revisionshapewillremain.
ASSERT_TRUE(shapes[1]->get_IsMoveFromRevision());
ASSERT_FALSE(shapes[1]->get_IsMoveToRevision());

