Shows how to resize a shape with an image. //Whenweinsertanimageusingthe"InsertImage"method,thebuilderscalestheshapethatdisplaystheimagesothat,
//whenweviewthedocumentusing100%zoominMicrosoftWord,theshapedisplaystheimageinitsactualsize.
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);
SharedPtr<Shape>shape=builder->InsertImage(ImageDir+u"Logo.jpg");

//A400x400imagewillcreateanImageDataobjectwithanimagesizeof300x300pt.
SharedPtr<ImageSize>imageSize=shape->get_ImageData()->get_ImageSize();

ASPOSE_ASSERT_EQ(300.0,imageSize->get_WidthPoints());
ASPOSE_ASSERT_EQ(300.0,imageSize->get_HeightPoints());

//Ifashape'sdimensionsmatchtheimagedata'sdimensions,
//thentheshapeisdisplayingtheimageinitsoriginalsize.
ASPOSE_ASSERT_EQ(300.0,shape->get_Width());
ASPOSE_ASSERT_EQ(300.0,shape->get_Height());

//Reducetheoverallsizeoftheshapeby50%.
System::setter_mul_wrap(shape.GetPointer(),&ShapeBase::get_Width,&ShapeBase::set_Width,0.5);

//Scalingfactorsapplytoboththewidthandtheheightatthesametimetopreservetheshape'sproportions.
ASPOSE_ASSERT_EQ(150.0,shape->get_Width());
ASPOSE_ASSERT_EQ(150.0,shape->get_Height());

//Whenweresizetheshape,thesizeoftheimagedataremainsthesame.
ASPOSE_ASSERT_EQ(300.0,imageSize->get_WidthPoints());
ASPOSE_ASSERT_EQ(300.0,imageSize->get_HeightPoints());

//Wecanreferencetheimagedatadimensionstoapplyascalingbasedonthesizeoftheimage.
shape->set_Width(imageSize->get_WidthPoints()*1.1);

ASPOSE_ASSERT_EQ(330.0,shape->get_Width());
ASPOSE_ASSERT_EQ(330.0,shape->get_Height());

doc->Save(ArtifactsDir+u"Image.ScaleImage.docx");

Examples


Shows how to replace all textbox shapes with image shapes. autodoc=MakeObject<Document>(MyDir+u"Textboxesindrawingcanvas.docx");

ArrayPtr<SharedPtr<Shape>>shapes=doc->GetChildNodes(NodeType::Shape,true)->LINQ_OfType<SharedPtr<Shape>>()->LINQ_ToArray();

autoisTextBox=[](SharedPtr<Shape>s)
{
returns->get_ShapeType()==ShapeType::TextBox;
};
autoisImage=[](SharedPtr<Shape>s)
{
returns->get_ShapeType()==ShapeType::Image;
};
ASSERT_EQ(3,shapes->LINQ_Count(isTextBox));
ASSERT_EQ(1,shapes->LINQ_Count(isImage));

for(SharedPtr<Shape>shape:shapes)
{
if(shape->get_ShapeType()==ShapeType::TextBox)
{
autoreplacementShape=MakeObject<Shape>(doc,ShapeType::Image);
replacementShape->get_ImageData()->SetImage(ImageDir+u"Logo.jpg");
replacementShape->set_Left(shape->get_Left());
replacementShape->set_Top(shape->get_Top());
replacementShape->set_Width(shape->get_Width());
replacementShape->set_Height(shape->get_Height());
replacementShape->set_RelativeHorizontalPosition(shape->get_RelativeHorizontalPosition());
replacementShape->set_RelativeVerticalPosition(shape->get_RelativeVerticalPosition());
replacementShape->set_HorizontalAlignment(shape->get_HorizontalAlignment());
replacementShape->set_VerticalAlignment(shape->get_VerticalAlignment());
replacementShape->set_WrapType(shape->get_WrapType());
replacementShape->set_WrapSide(shape->get_WrapSide());

shape->get_ParentNode()->InsertAfter(replacementShape,shape);
shape->Remove();
}
}

shapes=doc->GetChildNodes(NodeType::Shape,true)->LINQ_OfType<SharedPtr<Shape>>()->LINQ_ToArray();

ASSERT_EQ(0,shapes->LINQ_Count(isTextBox));
ASSERT_EQ(4,shapes->LINQ_Count(isImage));

doc->Save(ArtifactsDir+u"Shape.ReplaceTextboxesWithImages.docx");

