Examples


Shows how to create a line for a signature and insert it into a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

autooptions=MakeObject<SignatureLineOptions>();
options->set_AllowComments(true);
options->set_DefaultInstructions(true);
options->set_Email(u"john.doe@management.com");
options->set_Instructions(u"Pleasesignhere");
options->set_ShowDate(true);
options->set_Signer(u"JohnDoe");
options->set_SignerTitle(u"SeniorManager");

//Insertashapethatwillcontainasignatureline,whoseappearancewewill
//customizeusingthe"SignatureLineOptions"objectwehavecreatedabove.
//Ifweinsertashapewhosecoordinatesoriginateatthebottomrighthandcornerofthepage,
//wewillneedtosupplynegativexandycoordinatestobringtheshapeintoview.
SharedPtr<Shape>shape=builder->InsertSignatureLine(options,RelativeHorizontalPosition::RightMargin,-170.0,RelativeVerticalPosition::BottomMargin,
-60.0,WrapType::None);

ASSERT_TRUE(shape->get_IsSignatureLine());

//VerifythepropertiesofoursignaturelineviaitsShapeobject.
SharedPtr<SignatureLine>signatureLine=shape->get_SignatureLine();

ASSERT_EQ(u"john.doe@management.com",signatureLine->get_Email());
ASSERT_EQ(u"JohnDoe",signatureLine->get_Signer());
ASSERT_EQ(u"SeniorManager",signatureLine->get_SignerTitle());
ASSERT_EQ(u"Pleasesignhere",signatureLine->get_Instructions());
ASSERT_TRUE(signatureLine->get_ShowDate());
ASSERT_TRUE(signatureLine->get_AllowComments());
ASSERT_TRUE(signatureLine->get_DefaultInstructions());

doc->Save(ArtifactsDir+u"Shape.SignatureLine.docx");

