Examples


Show how to set marker formatting. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Scatter,432,252);
SharedPtr<Chart>chart=shape->get_Chart();

//Deletedefaultgeneratedseries.
chart->get_Series()->Clear();
SharedPtr<ChartSeries>series=
chart->get_Series()->Add(u"AWSeries1",MakeArray<double>({0.7,1.8,2.6,3.9}),MakeArray<double>({2.7,3.2,0.8,1.7}));

//Setmarkerformatting.
series->get_Marker()->set_Size(40);
series->get_Marker()->set_Symbol(MarkerSymbol::Square);
SharedPtr<ChartDataPointCollection>dataPoints=series->get_DataPoints();
dataPoints->idx_get(0)->get_Marker()->get_Format()->get_Fill()->PresetTextured(PresetTexture::Denim);
dataPoints->idx_get(0)->get_Marker()->get_Format()->get_Stroke()->set_ForeColor(System::Drawing::Color::get_Yellow());
dataPoints->idx_get(0)->get_Marker()->get_Format()->get_Stroke()->set_BackColor(System::Drawing::Color::get_Red());
dataPoints->idx_get(1)->get_Marker()->get_Format()->get_Fill()->PresetTextured(PresetTexture::WaterDroplets);
dataPoints->idx_get(1)->get_Marker()->get_Format()->get_Stroke()->set_ForeColor(System::Drawing::Color::get_Yellow());
dataPoints->idx_get(1)->get_Marker()->get_Format()->get_Stroke()->set_Visible(false);
dataPoints->idx_get(2)->get_Marker()->get_Format()->get_Fill()->PresetTextured(PresetTexture::GreenMarble);
dataPoints->idx_get(2)->get_Marker()->get_Format()->get_Stroke()->set_ForeColor(System::Drawing::Color::get_Yellow());
dataPoints->idx_get(3)->get_Marker()->get_Format()->get_Fill()->PresetTextured(PresetTexture::Oak);
dataPoints->idx_get(3)->get_Marker()->get_Format()->get_Stroke()->set_ForeColor(System::Drawing::Color::get_Yellow());
dataPoints->idx_get(3)->get_Marker()->get_Format()->get_Stroke()->set_Transparency(0.5);

doc->Save(ArtifactsDir+u"Charts.MarkerFormatting.docx");

Examples


Shows to create a variety of shapes. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Belowarefourexamplesofshapesthatwecaninsertintoourdocuments.
//1-Dotted,horizontal,half-transparentredline
//withanarrowontheleftendandadiamondontherightend:
autoarrow=MakeObject<Shape>(doc,ShapeType::Line);
arrow->set_Width(200);
arrow->get_Stroke()->set_Color(System::Drawing::Color::get_Red());
arrow->get_Stroke()->set_StartArrowType(ArrowType::Arrow);
arrow->get_Stroke()->set_StartArrowLength(ArrowLength::Long);
arrow->get_Stroke()->set_StartArrowWidth(ArrowWidth::Wide);
arrow->get_Stroke()->set_EndArrowType(ArrowType::Diamond);
arrow->get_Stroke()->set_EndArrowLength(ArrowLength::Long);
arrow->get_Stroke()->set_EndArrowWidth(ArrowWidth::Wide);
arrow->get_Stroke()->set_DashStyle(DashStyle::Dash);
arrow->get_Stroke()->set_Opacity(0.5);

ASSERT_EQ(JoinStyle::Miter,arrow->get_Stroke()->get_JoinStyle());

builder->InsertNode(arrow);

//2-Thickblackdiagonallinewithroundedends:
autoline=MakeObject<Shape>(doc,ShapeType::Line);
line->set_Top(40);
line->set_Width(200);
line->set_Height(20);
line->set_StrokeWeight(5.0);
line->get_Stroke()->set_EndCap(EndCap::Round);

builder->InsertNode(line);

//3-Arrowwithagreenfill:
autofilledInArrow=MakeObject<Shape>(doc,ShapeType::Arrow);
filledInArrow->set_Width(200);
filledInArrow->set_Height(40);
filledInArrow->set_Top(100);
filledInArrow->get_Fill()->set_ForeColor(System::Drawing::Color::get_Green());
filledInArrow->get_Fill()->set_Visible(true);

builder->InsertNode(filledInArrow);

//4-ArrowwithaflippedorientationfilledinwiththeAsposelogo:
autofilledInArrowImg=MakeObject<Shape>(doc,ShapeType::Arrow);
filledInArrowImg->set_Width(200);
filledInArrowImg->set_Height(40);
filledInArrowImg->set_Top(160);
filledInArrowImg->set_FlipOrientation(FlipOrientation::Both);

ArrayPtr<uint8_t>imageBytes=System::IO::File::ReadAllBytes(ImageDir+u"Logo.jpg");

{
autostream=MakeObject<System::IO::MemoryStream>(imageBytes);
SharedPtr<System::Drawing::Image>image=System::Drawing::Image::FromStream(stream);
//Whenwefliptheorientationofourarrow,wealsofliptheimagethatthearrowcontains.
//Fliptheimagetheotherwaytocancelthisoutbeforegettingtheshapetodisplayit.
image->RotateFlip(System::Drawing::RotateFlipType::RotateNoneFlipXY);

filledInArrowImg->get_ImageData()->SetImage(image);
filledInArrowImg->get_Stroke()->set_JoinStyle(JoinStyle::Round);

builder->InsertNode(filledInArrowImg);
}

doc->Save(ArtifactsDir+u"Drawing.VariousShapes.docx");

