shape->get_TextPath()->set_Kerning(true);

shape=AppendWordArt(doc,u"Nokerning:VAV",u"TimesNewRoman",100,24,System::Drawing::Color::get_Orange(),System::Drawing::Color::get_Red(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_Kerning(false);

//Usethe"Spacing"propertytosetthecustomspacingbetweencharactersonascalefrom0.0(none)to1.0(default).
shape=AppendWordArt(doc,u"Spacingsetto0.1",u"Calibri",120,24,System::Drawing::Color::get_BlueViolet(),System::Drawing::Color::get_Blue(),
ShapeType::TextCascadeDown);
shape->get_TextPath()->set_Spacing(0.1);

//Setthe"RotateLetters"propertyto"true"torotateeachcharacter90degreescounterclockwise.
shape=AppendWordArt(doc,u"RotateLetters",u"Calibri",200,36,System::Drawing::Color::get_GreenYellow(),System::Drawing::Color::get_Green(),
ShapeType::TextWave);
shape->get_TextPath()->set_RotateLetters(true);

//Setthe"SameLetterHeights"propertyto"true"togetthex-heightofeachcharactertoequalthecapheight.
shape=AppendWordArt(doc,u"SamecharacterheightforlowerandUPPERcase",u"Calibri",300,24,System::Drawing::Color::get_DeepSkyBlue(),
System::Drawing::Color::get_DodgerBlue(),ShapeType::TextSlantUp);
shape->get_TextPath()->set_SameLetterHeights(true);

//Bydefault,thetext'ssizewillalwaysscaletofitthecontainingshape'ssize,overridingthetextsizesetting.
shape=AppendWordArt(doc,u"FitShapeon",u"Calibri",160,24,System::Drawing::Color::get_LightBlue(),System::Drawing::Color::get_Blue(),
ShapeType::TextPlainText);
ASSERT_TRUE(shape->get_TextPath()->get_FitShape());
shape->get_TextPath()->set_Size(24.0);

//Ifwesetthe"FitShape:propertyto"false",thetextwillkeepthesize
//whichthe"Size"propertyspecifiesregardlessofthesizeoftheshape.
//Usethe"TextPathAlignment"propertyalsotoalignthetexttoasideoftheshape.
shape=AppendWordArt(doc,u"FitShapeoff",u"Calibri",160,24,System::Drawing::Color::get_LightBlue(),System::Drawing::Color::get_Blue(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_FitShape(false);
shape->get_TextPath()->set_Size(24.0);
shape->get_TextPath()->set_TextPathAlignment(TextPathAlignment::Right);

doc->Save(ArtifactsDir+u"Shape.InsertTextPaths.docx");
}

staticSharedPtr<Shape>AppendWordArt(SharedPtr<Document>doc,Stringtext,StringtextFontFamily,doubleshapeWidth,doubleshapeHeight,
System::Drawing::ColorwordArtFill,System::Drawing::Colorline,ShapeTypewordArtShapeType)
{
//CreateaninlineShape,whichwillserveasacontainerforourWordArt.
//TheshapecanonlybeavalidWordArtshapeifweassignaWordArt-designatedShapeTypetoit.
//Thesetypeswillhave"WordArtobject"inthedescription,
//andtheirenumeratorconstantnameswillallstartwith"Text".
autoshape=MakeObject<Shape>(doc,wordArtShapeType);
shape->set_WrapType(WrapType::Inline);
shape->set_Width(shapeWidth);
shape->set_Height(shapeHeight);
shape->set_FillColor(wordArtFill);
shape->set_StrokeColor(line);

shape->get_TextPath()->set_Text(text);
shape->get_TextPath()->set_FontFamily(textFontFamily);

autopara=System::DynamicCast<Paragraph>(doc->get_FirstSection()->get_Body()->AppendChild(MakeObject<Paragraph>(doc)));
para->AppendChild(shape);
returnshape;
}

Aspose::Words::Drawing::Shape::get_TextPath

Examples


Shows how to work with WordArt. voidInsertTextPaths()
{
autodoc=MakeObject<Document>();

//InsertaWordArtobjecttodisplaytextinashapethatwecanre-sizeandmovebyusingthemouseinMicrosoftWord.
//Providea"ShapeType"asanargumenttosetashapefortheWordArt.
SharedPtr<Shape>shape=AppendWordArt(doc,u"HelloWorld!Thistextisbold,anditalic.",u"Arial",480,24,System::Drawing::Color::get_White(),
System::Drawing::Color::get_Black(),ShapeType::TextPlainText);

//Applythe"Bold'and"Italic"formattingsettingstothetextusingtherespectiveproperties.
shape->get_TextPath()->set_Bold(true);
shape->get_TextPath()->set_Italic(true);

//Belowarevariousothertextformatting-relatedproperties.
ASSERT_FALSE(shape->get_TextPath()->get_Underline());
ASSERT_FALSE(shape->get_TextPath()->get_Shadow());
ASSERT_FALSE(shape->get_TextPath()->get_StrikeThrough());
ASSERT_FALSE(shape->get_TextPath()->get_ReverseRows());
ASSERT_FALSE(shape->get_TextPath()->get_XScale());
ASSERT_FALSE(shape->get_TextPath()->get_Trim());
ASSERT_FALSE(shape->get_TextPath()->get_SmallCaps());

ASPOSE_ASSERT_EQ(36.0,shape->get_TextPath()->get_Size());
ASSERT_EQ(u"HelloWorld!Thistextisbold,anditalic.",shape->get_TextPath()->get_Text());
ASSERT_EQ(ShapeType::TextPlainText,shape->get_ShapeType());

//Usethe"On"propertytoshow/hidethetext.
shape=AppendWordArt(doc,u"Onsetto\"true\"",u"Calibri",150,24,System::Drawing::Color::get_Yellow(),System::Drawing::Color::get_Red(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_On(true);

shape=AppendWordArt(doc,u"Onsetto\"false\"",u"Calibri",150,24,System::Drawing::Color::get_Yellow(),System::Drawing::Color::get_Purple(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_On(false);

//Usethe"Kerning"propertytoenable/disablekerningspacingbetweencertaincharacters.
shape=AppendWordArt(doc,u"Kerning:VAV",u"TimesNewRoman",90,24,System::Drawing::Color::get_Orange(),System::Drawing::Color::get_Red(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_Kerning(true);

shape=AppendWordArt(doc,u"Nokerning:VAV",u"TimesNewRoman",100,24,System::Drawing::Color::get_Orange(),System::Drawing::Color::get_Red(),
ShapeType::TextPlainText);
shape->get_TextPath()->set_Kerning(false);

//Usethe"Spacing"propertytosetthecustomspacingbetweencharactersonascalefrom0.0(none)to1.0(default).
shape=AppendWordArt(doc,u"Spacingsetto0.1",u"Calibri",120,24,System::Drawing::Color::get_BlueViolet(),System::Drawing::Color::get_Blue(),
ShapeType::TextCascadeDown);
shape->get_TextPath()->set_Spacing(0.1);

//Setthe"RotateLetters"propertyto"true"torotateeachcharacter90degreescounterclockwise.
shape=AppendWordArt(doc,u"RotateLetters",u"Calibri",200,36,System::Drawing::Color::get_GreenYellow(),System::Drawing::Color::get_Green(),
ShapeType::TextWave);
shape->get_TextPath()->set_RotateLetters(true);

//Setthe"SameLetterHeights"propertyto"true"togetthex-heightofeachcharactertoequalthecapheight.
shape=AppendWordArt(doc,u"SamecharacterheightforlowerandUPPERcase",u"Calibri",300,24,System::Drawing::Color::get_DeepSkyBlue(),
System::Drawing::Color::get_DodgerBlue(),ShapeType::TextSlantUp);
shape->get_TextPath()->set_SameLetterHeights(true);

//Bydefault,thetext'ssizewillalwaysscaletofitthecontainingshape'ssize,overridingthetextsizesetting.
shape=AppendWordArt(doc,u"FitShapeon",u"Calibri",160,24,System::Drawing::Color::get_LightBlue(),System::Drawing::Color::get_Blue(),
ShapeType::TextPlainText);
ASSERT_TRUE(shape->get_TextPath()->get_FitShape());
shape->get_TextPath()->set_Size(24.0);

//Ifwesetthe"FitShape:propertyto"false",thetextwillkeepthesize
//whichthe"Size"propertyspecifiesregardlessofthesizeoftheshape.
//Usethe"TextPathAlignment"propertyalsotoalignthetexttoasideoftheshape.
shape=AppendWordArt(doc,u"FitShapeoff",u"Calibri",160,24,System::Drawing::Color::get_LightBlue(),System::Drawing::Color::get_Blue(),
ShapeType::TextPlainText);
