detaileddescription.para:
Examples


Shows how to organize a document using AUTONUMLGL fields. voidFieldAutoNumLgl_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

constStringfillerText=
String(u"Loremipsumdolorsitamet,consecteturadipiscingelit,seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.")+
u"\nUtenimadminimveniam,quisnostrudexercitationullamcolaborisnisiutaliquipexeacommodoconsequat.";

//AUTONUMLGLfieldsdisplayanumberthatincrementsateachAUTONUMLGLfieldwithinitscurrentheadinglevel.
//Thesefieldsmaintainaseparatecountforeachheadinglevel,
//andeachfieldalsodisplaystheAUTONUMLGLfieldcountsforallheadinglevelsbelowitsown.
//Changingthecountforanyheadinglevelresetsthecountsforalllevelsabovethatlevelto1.
//Thisallowsustoorganizeourdocumentintheformofanoutlinelist.
//ThisisthefirstAUTONUMLGLfieldataheadinglevelof1,displaying"1."inthedocument.
InsertNumberedClause(builder,u"\tHeading1",fillerText,StyleIdentifier::Heading1);

//ThisisthesecondAUTONUMLGLfieldataheadinglevelof1,soitwilldisplay"2.".
InsertNumberedClause(builder,u"\tHeading2",fillerText,StyleIdentifier::Heading1);

//ThisisthefirstAUTONUMLGLfieldataheadinglevelof2,
//andtheAUTONUMLGLcountfortheheadinglevelbelowitis"2",soitwilldisplay"2.1.".
InsertNumberedClause(builder,u"\tHeading3",fillerText,StyleIdentifier::Heading2);

//ThisisthefirstAUTONUMLGLfieldataheadinglevelof3.
//Workinginthesamewayasthefieldabove,itwilldisplay"2.1.1.".
InsertNumberedClause(builder,u"\tHeading4",fillerText,StyleIdentifier::Heading3);

//Thisfieldisataheadinglevelof2,anditsrespectiveAUTONUMLGLcountisat2,sothefieldwilldisplay"2.2.".
InsertNumberedClause(builder,u"\tHeading5",fillerText,StyleIdentifier::Heading2);

//IncrementingtheAUTONUMLGLcountforaheadinglevelbelowthisone
//hasresetthecountforthislevelsothatthisfieldwilldisplay"2.2.1.".
InsertNumberedClause(builder,u"\tHeading6",fillerText,StyleIdentifier::Heading3);

autoisAutoNumLegal=[](SharedPtr<Field>f)
{
returnf->get_Type()==FieldType::FieldAutoNumLegal;
};

for(autofield:System::IterateOver<FieldAutoNumLgl>(doc->get_Range()->get_Fields()->LINQ_Where(isAutoNumLegal)))
{
//Theseparatorcharacter,whichappearsinthefieldresultimmediatelyafterthenumber,
//isafullstopbydefault.Ifweleavethispropertynull,
//ourlastAUTONUMLGLfieldwilldisplay"2.2.1."inthedocument.
ASSERT_TRUE(field->get_SeparatorCharacter()==nullptr);

//Settingacustomseparatorcharacterandremovingthetrailingperiod
//willchangethatfield'sappearancefrom"2.2.1."to"2:2:1".
//Wewillapplythistoallthefieldsthatwehavecreated.
field->set_SeparatorCharacter(u":");
field->set_RemoveTrailingPeriod(true);
ASSERT_EQ(u"AUTONUMLGL\\s:\\e",field->GetFieldCode());
}

doc->Save(ArtifactsDir+u"Field.AUTONUMLGL.docx");
}

staticvoidInsertNumberedClause(SharedPtr<DocumentBuilder>builder,Stringheading,Stringcontents,StyleIdentifierheadingStyle)
{
builder->InsertField(FieldType::FieldAutoNumLegal,true);
builder->get_CurrentParagraph()->get_ParagraphFormat()->set_StyleIdentifier(headingStyle);
builder->Writeln(heading);

//Thistextwillbelongtotheautonumlegalfieldaboveit.
//ItwillcollapsewhenweclickthearrownexttothecorrespondingAUTONUMLGLfieldinMicrosoftWord.
builder->get_CurrentParagraph()->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::BodyText);
builder->Writeln(contents);
}

