detaileddescription.para:
Examples


Shows how to work with CITATION and BIBLIOGRAPHY fields. //Openadocumentcontainingbibliographicalsourcesthatwecanfindin
//MicrosoftWordviaReferences->Citations&Bibliography->ManageSources.
autodoc=MakeObject<Document>(MyDir+u"Bibliography.docx");

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Write(u"Texttobecitedwithonesource.");

//Createacitationwithjustthepagenumberandtheauthorofthereferencedbook.
autofieldCitation=System::DynamicCast<FieldCitation>(builder->InsertField(FieldType::FieldCitation,true));

//Werefertosourcesusingtheirtagnames.
fieldCitation->set_SourceTag(u"Book1");
fieldCitation->set_PageNumber(u"85");
fieldCitation->set_SuppressAuthor(false);
fieldCitation->set_SuppressTitle(true);
fieldCitation->set_SuppressYear(true);

ASSERT_EQ(u"CITATIONBook1\\p85\\t\\y",fieldCitation->GetFieldCode());

//Createamoredetailedcitationwhichcitestwosources.
builder->InsertParagraph();
builder->Write(u"Texttobecitedwithtwosources.");
fieldCitation=System::DynamicCast<FieldCitation>(builder->InsertField(FieldType::FieldCitation,true));
fieldCitation->set_SourceTag(u"Book1");
fieldCitation->set_AnotherSourceTag(u"Book2");
fieldCitation->set_FormatLanguageId(u"en-US");
fieldCitation->set_PageNumber(u"19");
fieldCitation->set_Prefix(u"Prefix");
fieldCitation->set_Suffix(u"Suffix");
fieldCitation->set_SuppressAuthor(false);
fieldCitation->set_SuppressTitle(false);
fieldCitation->set_SuppressYear(false);
fieldCitation->set_VolumeNumber(u"VII");

ASSERT_EQ(u"CITATIONBook1\\mBook2\\len-US\\p19\\f\"Prefix\"\\s\"Suffix\"\\vVII",fieldCitation->GetFieldCode());

//WecanuseaBIBLIOGRAPHYfieldtodisplayallthesourceswithinthedocument.
builder->InsertBreak(BreakType::PageBreak);
autofieldBibliography=System::DynamicCast<FieldBibliography>(builder->InsertField(FieldType::FieldBibliography,true));
fieldBibliography->set_FormatLanguageId(u"1124");

ASSERT_EQ(u"BIBLIOGRAPHY\\l1124",fieldBibliography->GetFieldCode());

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.CITATION.docx");

