detaileddescription.para:
Examples


Shows how to remove fields from a field collection. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->InsertField(u"DATE\\@\"dddd,dMMMMyyyy\"");
builder->InsertField(u"TIME");
builder->InsertField(u"REVNUM");
builder->InsertField(u"AUTHOR\"JohnDoe\"");
builder->InsertField(u"SUBJECT\"MySubject\"");
builder->InsertField(u"QUOTE\"Helloworld!\"");
doc->UpdateFields();

SharedPtr<FieldCollection>fields=doc->get_Range()->get_Fields();

ASSERT_EQ(6,fields->get_Count());

//Belowarefourwaysofremovingfieldsfromafieldcollection.
//1-Getafieldtoremoveitself:
fields->idx_get(0)->Remove();
ASSERT_EQ(5,fields->get_Count());

//2-Getthecollectiontoremoveafieldthatwepasstoitsremovalmethod:
SharedPtr<Field>lastField=fields->idx_get(3);
fields->Remove(lastField);
ASSERT_EQ(4,fields->get_Count());

//3-Removeafieldfromacollectionatanindex:
fields->RemoveAt(2);
ASSERT_EQ(3,fields->get_Count());

//4-Removeallthefieldsfromthecollectionatonce:
fields->Clear();
ASSERT_EQ(0,fields->get_Count());

Examples


Shows how to work with a collection of fields. voidFieldCollection_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->InsertField(u"DATE\\@\"dddd,dMMMMyyyy\"");
builder->InsertField(u"TIME");
builder->InsertField(u"REVNUM");
builder->InsertField(u"AUTHOR\"JohnDoe\"");
builder->InsertField(u"SUBJECT\"MySubject\"");
builder->InsertField(u"QUOTE\"Helloworld!\"");
doc->UpdateFields();

SharedPtr<FieldCollection>fields=doc->get_Range()->get_Fields();

ASSERT_EQ(6,fields->get_Count());

//Iterateoverthefieldcollection,andprintcontentsandtype
//ofeveryfieldusingacustomvisitorimplementation.
autofieldVisitor=MakeObject<ExField::FieldVisitor>();

{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<Field>>>fieldEnumerator=fields->GetEnumerator();
while(fieldEnumerator->MoveNext())
{
if(fieldEnumerator->get_Current()!=nullptr)
{
fieldEnumerator->get_Current()->get_Start()->Accept(fieldVisitor);
if(fieldEnumerator->get_Current()->get_Separator()!=nullptr)
{
fieldEnumerator->get_Current()->get_Separator()->Accept(fieldVisitor);
}
fieldEnumerator->get_Current()->get_End()->Accept(fieldVisitor);
}
else
{
std::cout<<"Therearenofieldsinthedocument."<<std::endl;
}
}
}

std::cout<<fieldVisitor->GetText()<<std::endl;
}

classFieldVisitor:publicDocumentVisitor
{
public:
FieldVisitor()
{
mBuilder=MakeObject<System::Text::StringBuilder>();
}

StringGetText()
{
returnmBuilder->ToString();
}

VisitorActionVisitFieldStart(SharedPtr<FieldStart>fieldStart)override
{
mBuilder->AppendLine(String(u"Foundfield:")+System::ObjectExt::ToString(fieldStart->get_FieldType()));
mBuilder->AppendLine(String(u"\tFieldcode:")+fieldStart->GetField()->GetFieldCode());
mBuilder->AppendLine(String(u"\tDisplayedas:")+fieldStart->GetField()->get_Result());

returnVisitorAction::Continue;
}

VisitorActionVisitFieldSeparator(SharedPtr<FieldSeparator>fieldSeparator)override
{
mBuilder->AppendLine(String(u"\tFoundseparator:")+fieldSeparator->GetText());

returnVisitorAction::Continue;
}

VisitorActionVisitFieldEnd(SharedPtr<FieldEnd>fieldEnd)override
{
mBuilder->AppendLine(String(u"Endoffield:")+System::ObjectExt::ToString(fieldEnd->get_FieldType()));

returnVisitorAction::Continue;
}

private:
SharedPtr<System::Text::StringBuilder>mBuilder;
};

