detaileddescription.para:
Examples


Shows how to insert a DISPLAYBARCODE field, and set its properties. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

autofield=System::DynamicCast<FieldDisplayBarcode>(builder->InsertField(FieldType::FieldDisplayBarcode,true));

//Belowarefourtypesofbarcodes,decoratedinvariousways,thattheDISPLAYBARCODEfieldcandisplay.
//1-QRcodewithcustomcolors:
field->set_BarcodeType(u"QR");
field->set_BarcodeValue(u"ABC123");
field->set_BackgroundColor(u"0xF8BD69");
field->set_ForegroundColor(u"0xB5413B");
field->set_ErrorCorrectionLevel(u"3");
field->set_ScalingFactor(u"250");
field->set_SymbolHeight(u"1000");
field->set_SymbolRotation(u"0");

ASSERT_EQ(u"DISPLAYBARCODEABC123QR\\b0xF8BD69\\f0xB5413B\\q3\\s250\\h1000\\r0",field->GetFieldCode());
builder->Writeln();

//2-EAN13barcode,withthedigitsdisplayedbelowthebars:
field=System::DynamicCast<FieldDisplayBarcode>(builder->InsertField(FieldType::FieldDisplayBarcode,true));
field->set_BarcodeType(u"EAN13");
field->set_BarcodeValue(u"501234567890");
field->set_DisplayText(true);
field->set_PosCodeStyle(u"CASE");
field->set_FixCheckDigit(true);

ASSERT_EQ(u"DISPLAYBARCODE501234567890EAN13\\t\\pCASE\\x",field->GetFieldCode());
builder->Writeln();

//3-CODE39barcode:
field=System::DynamicCast<FieldDisplayBarcode>(builder->InsertField(FieldType::FieldDisplayBarcode,true));
field->set_BarcodeType(u"CODE39");
field->set_BarcodeValue(u"12345ABCDE");
field->set_AddStartStopChar(true);

ASSERT_EQ(u"DISPLAYBARCODE12345ABCDECODE39\\d",field->GetFieldCode());
builder->Writeln();

//4-ITF4barcode,withaspecifiedcasecode:
field=System::DynamicCast<FieldDisplayBarcode>(builder->InsertField(FieldType::FieldDisplayBarcode,true));
field->set_BarcodeType(u"ITF14");
field->set_BarcodeValue(u"09312345678907");
field->set_CaseCodeStyle(u"STD");

ASSERT_EQ(u"DISPLAYBARCODE09312345678907ITF14\\cSTD",field->GetFieldCode());

doc->Save(ArtifactsDir+u"Field.DISPLAYBARCODE.docx");

