detaileddescription.para:
Examples


Shows how to insert images using IMPORT and INCLUDEPICTURE fields. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Belowaretwosimilarfieldtypesthatwecanusetodisplayimageslinkedfromthelocalfilesystem.
//1-TheINCLUDEPICTUREfield:
autofieldIncludePicture=System::DynamicCast<FieldIncludePicture>(builder->InsertField(FieldType::FieldIncludePicture,true));
fieldIncludePicture->set_SourceFullName(ImageDir+u"Transparentbackgroundlogo.png");

ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(fieldIncludePicture->GetFieldCode(),u"INCLUDEPICTURE.*")->get_Success());

//ApplythePNG32.FLTfilter.
fieldIncludePicture->set_GraphicFilter(u"PNG32");
fieldIncludePicture->set_IsLinked(true);
fieldIncludePicture->set_ResizeHorizontally(true);
fieldIncludePicture->set_ResizeVertically(true);

//2-TheIMPORTfield:
autofieldImport=System::DynamicCast<FieldImport>(builder->InsertField(FieldType::FieldImport,true));
fieldImport->set_SourceFullName(ImageDir+u"Transparentbackgroundlogo.png");
fieldImport->set_GraphicFilter(u"PNG32");
fieldImport->set_IsLinked(true);

ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(fieldImport->GetFieldCode(),u"IMPORT.*\\\\cPNG32\\\\d")->get_Success());

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.IMPORT.INCLUDEPICTURE.docx");

