detaileddescription.para:
Examples


Shows how to create an INCLUDETEXT field, and set its properties. voidFieldIncludeText_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//BelowaretwowaystouseINCLUDETEXTfieldstodisplaythecontentsofanXMLfileinthelocalfilesystem.
//1-PerformanXSLtransformationonanXMLdocument:
SharedPtr<FieldIncludeText>fieldIncludeText=
CreateFieldIncludeText(builder,MyDir+u"CDcollectiondata.xml",false,u"text/xml",u"XML",u"ISO-8859-1");
fieldIncludeText->set_XslTransformation(MyDir+u"CDcollectionXSLtransformation.xsl");

builder->Writeln();

//2-UseanXPathtotakespecificelementsfromanXMLdocument:
fieldIncludeText=CreateFieldIncludeText(builder,MyDir+u"CDcollectiondata.xml",false,u"text/xml",u"XML",u"ISO-8859-1");
fieldIncludeText->set_NamespaceMappings(u"xmlns:n='myNamespace'");
fieldIncludeText->set_XPath(u"/catalog/cd/title");

doc->Save(ArtifactsDir+u"Field.INCLUDETEXT.docx");
}

SharedPtr<FieldIncludeText>CreateFieldIncludeText(SharedPtr<DocumentBuilder>builder,StringsourceFullName,boollockFields,StringmimeType,
StringtextConverter,Stringencoding)
{
autofieldIncludeText=System::DynamicCast<FieldIncludeText>(builder->InsertField(FieldType::FieldIncludeText,true));
fieldIncludeText->set_SourceFullName(sourceFullName);
fieldIncludeText->set_LockFields(lockFields);
fieldIncludeText->set_MimeType(mimeType);
fieldIncludeText->set_TextConverter(textConverter);
fieldIncludeText->set_Encoding(encoding);

returnfieldIncludeText;
}

