}

staticvoidInsertFieldLink(SharedPtr<DocumentBuilder>builder,ExField::InsertLinkedObjectAsinsertLinkedObjectAs,StringprogId,StringsourceFullName,
StringsourceItem,boolshouldAutoUpdate)
{
autofield=System::DynamicCast<FieldLink>(builder->InsertField(FieldType::FieldLink,true));

switch(insertLinkedObjectAs)
{
caseApiExamples::ExField::InsertLinkedObjectAs::Text:
field->set_InsertAsText(true);
break;

caseApiExamples::ExField::InsertLinkedObjectAs::Unicode:
field->set_InsertAsUnicode(true);
break;

caseApiExamples::ExField::InsertLinkedObjectAs::Html:
field->set_InsertAsHtml(true);
break;

caseApiExamples::ExField::InsertLinkedObjectAs::Rtf:
field->set_InsertAsRtf(true);
break;

caseApiExamples::ExField::InsertLinkedObjectAs::Picture:
field->set_InsertAsPicture(true);
break;

caseApiExamples::ExField::InsertLinkedObjectAs::Bitmap:
field->set_InsertAsBitmap(true);
break;
}

field->set_AutoUpdate(shouldAutoUpdate);
field->set_ProgId(progId);
field->set_SourceFullName(sourceFullName);
field->set_SourceItem(sourceItem);

builder->Writeln(u"\n");
}

