detaileddescription.para:
Examples


Shows to insert NOTEREF fields, and modify their appearance. voidFieldNoteRef_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//CreateabookmarkwithafootnotethattheNOTEREFfieldwillreference.
InsertBookmarkWithFootnote(builder,u"MyBookmark1",u"ContentsofMyBookmark1",u"FootnotefromMyBookmark1");

//ThisNOTEREFfieldwilldisplaythenumberofthefootnoteinsidethereferencedbookmark.
//SettingtheInsertHyperlinkpropertyletsusjumptothebookmarkbyCtrl+clickingthefieldinMicrosoftWord.
ASSERT_EQ(u"NOTEREFMyBookmark2\\h",
InsertFieldNoteRef(builder,u"MyBookmark2",true,false,false,u"HyperlinktoBookmark2,withfootnotenumber")->GetFieldCode());

//Whenusingthe\pflag,afterthefootnotenumber,thefieldalsodisplaysthebookmark'spositionrelativetothefield.
//Bookmark1isabovethisfieldandcontainsfootnotenumber1,sotheresultwillbe"1above"onupdate.
ASSERT_EQ(u"NOTEREFMyBookmark1\\h\\p",
InsertFieldNoteRef(builder,u"MyBookmark1",true,true,false,u"Bookmark1,withfootnotenumber")->GetFieldCode());

//Bookmark2isbelowthisfieldandcontainsfootnotenumber2,sothefieldwilldisplay"2below".
//The\fflagmakesthenumber2appearinthesameformatasthefootnotenumberlabelintheactualtext.
ASSERT_EQ(u"NOTEREFMyBookmark2\\h\\p\\f",
InsertFieldNoteRef(builder,u"MyBookmark2",true,true,true,u"Bookmark2,withfootnotenumber")->GetFieldCode());

builder->InsertBreak(BreakType::PageBreak);
InsertBookmarkWithFootnote(builder,u"MyBookmark2",u"ContentsofMyBookmark2",u"FootnotefromMyBookmark2");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.NOTEREF.docx");
}

staticSharedPtr<FieldNoteRef>InsertFieldNoteRef(SharedPtr<DocumentBuilder>builder,StringbookmarkName,boolinsertHyperlink,
boolinsertRelativePosition,boolinsertReferenceMark,StringtextBefore)
{
builder->Write(textBefore);

autofield=System::DynamicCast<FieldNoteRef>(builder->InsertField(FieldType::FieldNoteRef,true));
field->set_BookmarkName(bookmarkName);
field->set_InsertHyperlink(insertHyperlink);
field->set_InsertRelativePosition(insertRelativePosition);
field->set_InsertReferenceMark(insertReferenceMark);
builder->Writeln();

returnfield;
}

staticvoidInsertBookmarkWithFootnote(SharedPtr<DocumentBuilder>builder,StringbookmarkName,StringbookmarkText,StringfootnoteText)
{
builder->StartBookmark(bookmarkName);
builder->Write(bookmarkText);
builder->InsertFootnote(FootnoteType::Footnote,footnoteText);
builder->EndBookmark(bookmarkName);
builder->Writeln();
}

