detaileddescription.para:
Examples


Shows to insert PAGEREF fields to display the relative location of bookmarks. voidFieldPageRef_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

InsertAndNameBookmark(builder,u"MyBookmark1");

//InsertaPAGEREFfieldthatdisplayswhatpageabookmarkison.
//SettheInsertHyperlinkflagtomakethefieldalsofunctionasaclickablelinktothebookmark.
ASSERT_EQ(u"PAGEREFMyBookmark3\\h",InsertFieldPageRef(builder,u"MyBookmark3",true,false,u"HyperlinktoBookmark3,onpage:")->GetFieldCode());

//Wecanusethe\pflagtogetthePAGEREFfieldtodisplay
//thebookmark'spositionrelativetothepositionofthefield.
//Bookmark1isonthesamepageandabovethisfield,sothisfield'sdisplayedresultwillbe"above".
ASSERT_EQ(u"PAGEREFMyBookmark1\\h\\p",InsertFieldPageRef(builder,u"MyBookmark1",true,true,u"Bookmark1is")->GetFieldCode());

//Bookmark2willbeonthesamepageandbelowthisfield,sothisfield'sdisplayedresultwillbe"below".
ASSERT_EQ(u"PAGEREFMyBookmark2\\h\\p",InsertFieldPageRef(builder,u"MyBookmark2",true,true,u"Bookmark2is")->GetFieldCode());

//Bookmark3willbeonadifferentpage,sothefieldwilldisplay"onpage2".
ASSERT_EQ(u"PAGEREFMyBookmark3\\h\\p",InsertFieldPageRef(builder,u"MyBookmark3",true,true,u"Bookmark3is")->GetFieldCode());

InsertAndNameBookmark(builder,u"MyBookmark2");
builder->InsertBreak(BreakType::PageBreak);
InsertAndNameBookmark(builder,u"MyBookmark3");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.PAGEREF.docx");
}

staticSharedPtr<FieldPageRef>InsertFieldPageRef(SharedPtr<DocumentBuilder>builder,StringbookmarkName,boolinsertHyperlink,
boolinsertRelativePosition,StringtextBefore)
{
builder->Write(textBefore);

autofield=System::DynamicCast<FieldPageRef>(builder->InsertField(FieldType::FieldPageRef,true));
field->set_BookmarkName(bookmarkName);
field->set_InsertHyperlink(insertHyperlink);
field->set_InsertRelativePosition(insertRelativePosition);
builder->Writeln();

returnfield;
}

staticvoidInsertAndNameBookmark(SharedPtr<DocumentBuilder>builder,StringbookmarkName)
{
builder->StartBookmark(bookmarkName);
builder->Writeln(String::Format(u"Contentsofbookmark\"{0}\".",bookmarkName));
builder->EndBookmark(bookmarkName);
}

