builder->InsertFootnote(FootnoteType::Footnote,u"MyBookmarkfootnote#2");
builder->EndBookmark(u"MyBookmark");
builder->MoveToDocumentStart();

//Wewillapplyacustomlistformat,wheretheamountofanglebracketsindicatesthelistlevelwearecurrentlyat.
builder->get_ListFormat()->ApplyNumberDefault();
builder->get_ListFormat()->get_ListLevel()->set_NumberFormat(u">\x0000");

//InsertaREFfieldthatwillcontainthetextwithinourbookmark,actasahyperlink,andclonethebookmark'sfootnotes.
SharedPtr<FieldRef>field=InsertFieldRef(builder,u"MyBookmark",u"",u"\n");
field->set_IncludeNoteOrComment(true);
field->set_InsertHyperlink(true);

ASSERT_EQ(u"REFMyBookmark\\f\\h",field->GetFieldCode());

//InsertaREFfield,anddisplaywhetherthereferencedbookmarkisaboveorbelowit.
field=InsertFieldRef(builder,u"MyBookmark",u"Thereferencedparagraphis",u"thisfield.\n");
field->set_InsertRelativePosition(true);

ASSERT_EQ(u"REFMyBookmark\\p",field->GetFieldCode());

//Displaythelistnumberofthebookmarkasitappearsinthedocument.
field=InsertFieldRef(builder,u"MyBookmark",u"Thebookmark'sparagraphnumberis",u"\n");
field->set_InsertParagraphNumber(true);

ASSERT_EQ(u"REFMyBookmark\\n",field->GetFieldCode());

//Displaythebookmark'slistnumber,butwithnon-delimitercharacters,suchastheanglebrackets,omitted.
field=InsertFieldRef(builder,u"MyBookmark",u"Thebookmark'sparagraphnumber,non-delimiterssuppressed,is",u"\n");
field->set_InsertParagraphNumber(true);
field->set_SuppressNonDelimiters(true);

ASSERT_EQ(u"REFMyBookmark\\n\\t",field->GetFieldCode());

//Movedownonelistlevel.
builder->get_ListFormat()->set_ListLevelNumber(builder->get_ListFormat()->get_ListLevelNumber()+1);
builder->get_ListFormat()->get_ListLevel()->set_NumberFormat(u">>\x0001");

//Displaythelistnumberofthebookmarkandthenumbersofallthelistlevelsaboveit.
field=InsertFieldRef(builder,u"MyBookmark",u"Thebookmark'sfullcontextparagraphnumberis",u"\n");
field->set_InsertParagraphNumberInFullContext(true);

ASSERT_EQ(u"REFMyBookmark\\w",field->GetFieldCode());

builder->InsertBreak(BreakType::PageBreak);

//DisplaythelistlevelnumbersbetweenthisREFfield,andthebookmarkthatitisreferencing.
field=InsertFieldRef(builder,u"MyBookmark",u"Thebookmark'srelativeparagraphnumberis",u"\n");
field->set_InsertParagraphNumberInRelativeContext(true);

ASSERT_EQ(u"REFMyBookmark\\r",field->GetFieldCode());

//Attheendofthedocument,thebookmarkwillshowupasalistitemhere.
builder->Writeln(u"Listlevelabovebookmark");
builder->get_ListFormat()->set_ListLevelNumber(builder->get_ListFormat()->get_ListLevelNumber()+1);
builder->get_ListFormat()->get_ListLevel()->set_NumberFormat(u">>>\x0002");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.REF.docx");
}

staticSharedPtr<FieldRef>InsertFieldRef(SharedPtr<DocumentBuilder>builder,StringbookmarkName,StringtextBefore,StringtextAfter)
{
builder->Write(textBefore);
autofield=System::DynamicCast<FieldRef>(builder->InsertField(FieldType::FieldRef,true));
field->set_BookmarkName(bookmarkName);
builder->Write(textAfter);
returnfield;
}

