Shows how to create bookmarked text with a SET field, and then display it in the document using a REF field. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//NamebookmarkedtextwithaSETfield.
//Thisfieldreferstothe"bookmark"notabookmarkstructurethatappearswithinthetext,butanamedvariable.
autofieldSet=System::DynamicCast<FieldSet>(builder->InsertField(FieldType::FieldSet,false));
fieldSet->set_BookmarkName(u"MyBookmark");
fieldSet->set_BookmarkText(u"Helloworld!");
fieldSet->Update();

ASSERT_EQ(u"SETMyBookmark\"Helloworld!\"",fieldSet->GetFieldCode());

//RefertothebookmarkbynameinaREFfieldanddisplayitscontents.
autofieldRef=System::DynamicCast<FieldRef>(builder->InsertField(FieldType::FieldRef,true));
fieldRef->set_BookmarkName(u"MyBookmark");
fieldRef->Update();

ASSERT_EQ(u"REFMyBookmark",fieldRef->GetFieldCode());
ASSERT_EQ(u"Helloworld!",fieldRef->get_Result());

doc->Save(ArtifactsDir+u"Field.SET.REF.docx");
