detaileddescription.para:
Examples


Shows how to combine table of contents and sequence fields. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//ATOCfieldcancreateanentryinitstableofcontentsforeachSEQfieldfoundinthedocument.
//EachentrycontainstheparagraphthatcontainstheSEQfield,
//andthenumberofthepagethatthefieldappearson.
autofieldToc=System::DynamicCast<FieldToc>(builder->InsertField(FieldType::FieldTOC,true));

//ConfigurethisTOCfieldtohaveaSequenceIdentifierpropertywithavalueof"MySequence".
fieldToc->set_TableOfFiguresLabel(u"MySequence");

//ConfigurethisTOCfieldtoonlypickupSEQfieldsthatarewithintheboundsofabookmark
//named"TOCBookmark".
fieldToc->set_BookmarkName(u"TOCBookmark");
builder->InsertBreak(BreakType::PageBreak);

ASSERT_EQ(u"TOC\\cMySequence\\bTOCBookmark",fieldToc->GetFieldCode());

//SEQfieldsdisplayacountthatincrementsateachSEQfield.
//Thesefieldsalsomaintainseparatecountsforeachuniquenamedsequence
//identifiedbytheSEQfield's"SequenceIdentifier"property.
//InsertaSEQfieldthathasasequenceidentifierthatmatchestheTOC's
//TableOfFiguresLabelproperty.ThisfieldwillnotcreateanentryintheTOCsinceitisoutside
//thebookmark'sboundsdesignatedby"BookmarkName".
builder->Write(u"MySequence#");
autofieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
builder->Writeln(u",willnotshowupintheTOCbecauseitisoutsideofthebookmark.");

builder->StartBookmark(u"TOCBookmark");

//ThisSEQfield'ssequencematchestheTOC's"TableOfFiguresLabel"propertyandiswithinthebookmark'sbounds.
//TheparagraphthatcontainsthisfieldwillshowupintheTOCasanentry.
builder->Write(u"MySequence#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
builder->Writeln(u",willshowupintheTOCnexttotheentryfortheabovecaption.");

//ThisSEQfield'ssequencedoesnotmatchtheTOC's"TableOfFiguresLabel"property,
//andiswithintheboundsofthebookmark.ItsparagraphwillnotshowupintheTOCasanentry.
builder->Write(u"MySequence#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"OtherSequence");
builder->Writeln(u",willnotshowupintheTOCbecauseit'sfromadifferentsequenceidentifier.");

//ThisSEQfield'ssequencematchestheTOC's"TableOfFiguresLabel"propertyandiswithintheboundsofthebookmark.
//Thisfieldalsoreferencesanotherbookmark.ThecontentsofthatbookmarkwillappearintheTOCentryforthisSEQfield.
//TheSEQfielditselfwillnotdisplaythecontentsofthatbookmark.
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
fieldSeq->set_BookmarkName(u"SEQBookmark");
ASSERT_EQ(u"SEQMySequenceSEQBookmark",fieldSeq->GetFieldCode());

//CreateabookmarkwithcontentsthatwillshowupintheTOCentryduetotheaboveSEQfieldreferencingit.
builder->InsertBreak(BreakType::PageBreak);
builder->StartBookmark(u"SEQBookmark");
builder->Write(u"MySequence#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
builder->Writeln(u",textfrominsideSEQBookmark.");
builder->EndBookmark(u"SEQBookmark");

builder->EndBookmark(u"TOCBookmark");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.SEQ.Bookmark.docx");

Examples


Shows create numbering using SEQ fields. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//SEQfieldsdisplayacountthatincrementsateachSEQfield.
//Thesefieldsalsomaintainseparatecountsforeachuniquenamedsequence
//identifiedbytheSEQfield's"SequenceIdentifier"property.
//InsertaSEQfieldthatwilldisplaythecurrentcountvalueof"MySequence",
//afterusingthe"ResetNumber"propertytosetitto100.
builder->Write(u"#");
autofieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
fieldSeq->set_ResetNumber(u"100");
fieldSeq->Update();

ASSERT_EQ(u"SEQMySequence\\r100",fieldSeq->GetFieldCode());
ASSERT_EQ(u"100",fieldSeq->get_Result());

//DisplaythenextnumberinthissequencewithanotherSEQfield.
builder->Write(u",#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
fieldSeq->Update();

ASSERT_EQ(u"101",fieldSeq->get_Result());

//Insertalevel1heading.
builder->InsertBreak(BreakType::ParagraphBreak);
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"Heading1"));
builder->Writeln(u"Thislevel1headingwillresetMySequenceto1");
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"Normal"));

//InsertanotherSEQfieldfromthesamesequenceandconfigureittoresetthecountateveryheadingwith1.
builder->Write(u"\n#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
fieldSeq->set_ResetHeadingLevel(u"1");
fieldSeq->Update();

//Theaboveheadingisalevel1heading,sothecountforthissequenceisresetto1.
ASSERT_EQ(u"SEQMySequence\\s1",fieldSeq->GetFieldCode());
ASSERT_EQ(u"1",fieldSeq->get_Result());

//Movetothenextnumberofthissequence.
builder->Write(u",#");
fieldSeq=System::DynamicCast<FieldSeq>(builder->InsertField(FieldType::FieldSequence,true));
fieldSeq->set_SequenceIdentifier(u"MySequence");
fieldSeq->set_InsertNextNumber(true);
fieldSeq->Update();

ASSERT_EQ(u"SEQMySequence\\n",fieldSeq->GetFieldCode());
ASSERT_EQ(u"2",fieldSeq->get_Result());

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.SEQ.ResetNumbering.docx");

