Examples


Shows how to sort INDEX field entries phonetically. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//CreateanINDEXfieldwhichwilldisplayanentryforeachXEfieldfoundinthedocument.
//EachentrywilldisplaytheXEfield'sTextpropertyvalueontheleftside,
//andthenumberofthepagethatcontainstheXEfieldontheright.
//TheINDEXentrywillcollectallXEfieldswithmatchingvaluesinthe"Text"property
//intooneentryasopposedtomakinganentryforeachXEfield.
autoindex=System::DynamicCast<FieldIndex>(builder->InsertField(FieldType::FieldIndex,true));

//TheINDEXtableautomaticallysortsitsentriesbythevaluesoftheirTextpropertiesinalphabeticorder.
//SettheINDEXtabletosortentriesphoneticallyusingHiraganainstead.
index->set_UseYomi(sortEntriesUsingYomi);

if(sortEntriesUsingYomi)
{
ASSERT_EQ(u"INDEX\\y",index->GetFieldCode());
}
else
{
ASSERT_EQ(u"INDEX",index->GetFieldCode());
}

//Insert4XEfields,whichwouldshowupasentriesintheINDEXfield'stableofcontents.
//The"Text"propertymaycontainaword'sspellinginKanji,whosepronunciationmaybeambiguous,
//whilethe"Yomi"versionofthewordwillspellexactlyhowitispronouncedusingHiragana.
//IfwesetourINDEXfieldtouseYomi,itwillsorttheseentries
//bythevalueoftheirYomiproperties,insteadoftheirTextvalues.
builder->InsertBreak(BreakType::PageBreak);
autoindexEntry=System::DynamicCast<FieldXE>(builder->InsertField(FieldType::FieldIndexEntry,true));
indexEntry->set_Text(u"");
indexEntry->set_Yomi(u"");

ASSERT_EQ(u"XE\\y",indexEntry->GetFieldCode());

builder->InsertBreak(BreakType::PageBreak);
indexEntry=System::DynamicCast<FieldXE>(builder->InsertField(FieldType::FieldIndexEntry,true));
indexEntry->set_Text(u"");
indexEntry->set_Yomi(u"");

builder->InsertBreak(BreakType::PageBreak);
indexEntry=System::DynamicCast<FieldXE>(builder->InsertField(FieldType::FieldIndexEntry,true));
indexEntry->set_Text(u"");
indexEntry->set_Yomi(u"");

builder->InsertBreak(BreakType::PageBreak);
indexEntry=System::DynamicCast<FieldXE>(builder->InsertField(FieldType::FieldIndexEntry,true));
indexEntry->set_Text(u"");
indexEntry->set_Yomi(u"");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.INDEX.XE.Yomi.docx");

