break;

caseFieldType::FieldFormCheckBox:
AppendLine(String(u"\tCheckboxsize:")+formField->get_CheckBoxSize());
AppendLine(String(u"\t")+u"Checkboxiscurrently:"+(formField->get_Checked()?String(u"checked,"):String(u"unchecked,"))+
u"bydefault:"+(formField->get_Default()?String(u"checked"):String(u"unchecked")));
break;

caseFieldType::FieldFormTextInput:
AppendLine(String(u"\tInputformat:")+formField->get_TextInputFormat());
AppendLine(String(u"\tCurrentcontents:")+formField->get_Result());
break;

default:
break;
}

//Letthevisitorcontinuevisitingothernodes.
returnVisitorAction::Continue;
}

StringGetText()
{
returnmBuilder->ToString();
}

private:
SharedPtr<System::Text::StringBuilder>mBuilder;

voidAppendLine(Stringtext)
{
mBuilder->Append(text+u'\n');
}
};

Examples


Shows how to insert a combo box. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

