Examples


Shows how insert different kinds of form fields into a document, and process them with using a document visitor implementation. voidVisitor()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Useadocumentbuildertoinsertacombobox.
builder->Write(u"Chooseavaluefromthiscombobox:");
SharedPtr<FormField>comboBox=builder->InsertComboBox(u"MyComboBox",MakeArray<String>({u"One",u"Two",u"Three"}),0);
comboBox->set_CalculateOnExit(true);
ASSERT_EQ(3,comboBox->get_DropDownItems()->get_Count());
ASSERT_EQ(0,comboBox->get_DropDownSelectedIndex());
ASSERT_TRUE(comboBox->get_Enabled());

builder->InsertBreak(BreakType::ParagraphBreak);

//Useadocumentbuildertoinsertacheckbox.
builder->Write(u"Clickthischeckboxtotick/untickit:");
SharedPtr<FormField>checkBox=builder->InsertCheckBox(u"MyCheckBox",false,50);
checkBox->set_IsCheckBoxExactSize(true);
checkBox->set_HelpText(u"Rightclicktocheckthisbox");
checkBox->set_OwnHelp(true);
checkBox->set_StatusText(u"Checkboxstatustext");
checkBox->set_OwnStatus(true);
ASPOSE_ASSERT_EQ(50.0,checkBox->get_CheckBoxSize());
ASSERT_FALSE(checkBox->get_Checked());
ASSERT_FALSE(checkBox->get_Default());

builder->InsertBreak(BreakType::ParagraphBreak);

//Useadocumentbuildertoinserttextinputformfield.
builder->Write(u"Entertexthere:");
SharedPtr<FormField>textInput=builder->InsertTextInput(u"MyTextInput",TextFormFieldType::Regular,u"",u"Placeholdertext",50);
textInput->set_EntryMacro(u"EntryMacro");
textInput->set_ExitMacro(u"ExitMacro");
textInput->set_TextInputDefault(u"Regular");
textInput->set_TextInputFormat(u"FIRSTCAPITAL");
textInput->SetTextInputValue(System::ObjectExt::Box<String>(u"Newplaceholdertext"));
ASSERT_EQ(TextFormFieldType::Regular,textInput->get_TextInputType());
ASSERT_EQ(50,textInput->get_MaxLength());

//Thiscollectioncontainsallourformfields.
SharedPtr<FormFieldCollection>formFields=doc->get_Range()->get_FormFields();
ASSERT_EQ(3,formFields->get_Count());

//Fieldsdisplayourformfields.Wecanseetheirfieldcodesbyopeningthisdocument
//inMicrosoftandpressingAlt+F9.Thesefieldshavenoswitches,
//andmembersoftheFormFieldobjectfullygoverntheirformfields'content.
ASSERT_EQ(3,doc->get_Range()->get_Fields()->get_Count());
ASSERT_EQ(u"FORMDROPDOWN\u0001",doc->get_Range()->get_Fields()->idx_get(0)->GetFieldCode());
ASSERT_EQ(u"FORMCHECKBOX\u0001",doc->get_Range()->get_Fields()->idx_get(1)->GetFieldCode());
ASSERT_EQ(u"FORMTEXT\u0001",doc->get_Range()->get_Fields()->idx_get(2)->GetFieldCode());

//Alloweachformfieldtoacceptadocumentvisitor.
autoformFieldVisitor=MakeObject<ExFormFields::FormFieldVisitor>();

{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<FormField>>>fieldEnumerator=formFields->GetEnumerator();
while(fieldEnumerator->MoveNext())
{
fieldEnumerator->get_Current()->Accept(formFieldVisitor);
}
}

std::cout<<formFieldVisitor->GetText()<<std::endl;

doc->UpdateFields();
doc->Save(ArtifactsDir+u"FormFields.Visitor.html");
}

classFormFieldVisitor:publicDocumentVisitor
{
public:
FormFieldVisitor()
{
mBuilder=MakeObject<System::Text::StringBuilder>();
}

VisitorActionVisitFormField(SharedPtr<FormField>formField)override
{
AppendLine(System::ObjectExt::ToString(formField->get_Type())+u":\""+formField->get_Name()+u"\"");
AppendLine(String(u"\tStatus:")+(formField->get_Enabled()?String(u"Enabled"):String(u"Disabled")));
AppendLine(String(u"\tHelpText:")+formField->get_HelpText());
AppendLine(String(u"\tEntrymacroname:")+formField->get_EntryMacro());
AppendLine(String(u"\tExitmacroname:")+formField->get_ExitMacro());

switch(formField->get_Type())
{
caseFieldType::FieldFormDropDown:
AppendLine(String(u"\tDrop-downitemscount:")+formField->get_DropDownItems()->get_Count()+u",defaultselecteditemindex:"+
formField->get_DropDownSelectedIndex());
AppendLine(String(u"\tDrop-downitems:")+String::Join(u",",formField->get_DropDownItems()->LINQ_ToArray()));
break;

caseFieldType::FieldFormCheckBox:
AppendLine(String(u"\tCheckboxsize:")+formField->get_CheckBoxSize());
AppendLine(String(u"\t")+u"Checkboxiscurrently:"+(formField->get_Checked()?String(u"checked,"):String(u"unchecked,"))+
u"bydefault:"+(formField->get_Default()?String(u"checked"):String(u"unchecked")));
break;

caseFieldType::FieldFormTextInput:
AppendLine(String(u"\tInputformat:")+formField->get_TextInputFormat());
AppendLine(String(u"\tCurrentcontents:")+formField->get_Result());
break;

default:
break;
}

//Letthevisitorcontinuevisitingothernodes.
returnVisitorAction::Continue;
}

StringGetText()
{
returnmBuilder->ToString();
}

private:
SharedPtr<System::Text::StringBuilder>mBuilder;

voidAppendLine(Stringtext)
{
mBuilder->Append(text+u'\n');
}
};

