Stringpr_OriginalFormat;
Stringpr_NewValue;
};

public:
FieldResultFormatter(StringnumberFormat,StringdateFormat,StringgeneralFormat)
:mFormatInvocations(MakeObject<System::Collections::Generic::List<SharedPtr<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>>>())
{
mNumberFormat=numberFormat;
mDateFormat=dateFormat;
mGeneralFormat=generalFormat;
}

StringFormatNumeric(doublevalue,Stringformat)override
{
if(String::IsNullOrEmpty(mNumberFormat))
{
returnnullptr;
}

StringnewValue=String::Format(mNumberFormat,value);
get_FormatInvocations()->Add(MakeObject<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>(
ApiExamples::ExDocumentBuilder::FieldResultFormatter::FormatInvocationType::Numeric,System::ObjectExt::Box<double>(value),format,newValue));
returnnewValue;
}

StringFormatDateTime(System::DateTimevalue,Stringformat,CalendarTypecalendarType)override
{
if(String::IsNullOrEmpty(mDateFormat))
{
returnnullptr;
}

StringnewValue=String::Format(mDateFormat,value);
get_FormatInvocations()->Add(MakeObject<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>(
ApiExamples::ExDocumentBuilder::FieldResultFormatter::FormatInvocationType::DateTime,
System::ObjectExt::Box<String>(String::Format(u"{0}({1})",value,calendarType)),format,newValue));
returnnewValue;
}

StringFormat(Stringvalue,GeneralFormatformat)override
{
returnFormat(System::ObjectExt::Box<String>(value),format);
}

StringFormat(doublevalue,GeneralFormatformat)override
{
returnFormat(System::ObjectExt::Box<double>(value),format);
}

intCountFormatInvocations(ExDocumentBuilder::FieldResultFormatter::FormatInvocationTypeformatInvocationType)
{
if(formatInvocationType==ApiExamples::ExDocumentBuilder::FieldResultFormatter::FormatInvocationType::All)
{
returnget_FormatInvocations()->get_Count();
}

std::function<bool(SharedPtr<FormatInvocation>f)>hasValidFormatInvocationType=[&formatInvocationType](SharedPtr<FormatInvocation>f)
{
returnf->get_FormatInvocationType()==formatInvocationType;
};

returnget_FormatInvocations()->LINQ_Count(hasValidFormatInvocationType);
}

voidPrintFormatInvocations()
{
for(constauto&f:get_FormatInvocations())
{
std::cout<<(String::Format(u"Invocationtype:\t{0}\n",f->get_FormatInvocationType())+
String::Format(u"\tOriginalvalue:\t\t{0}\n",f->get_Value())+
String::Format(u"\tOriginalformat:\t{0}\n",f->get_OriginalFormat())+
String::Format(u"\tNewvalue:\t\t\t{0}\n",f->get_NewValue()))
<<std::endl;
}
}

private:
StringmNumberFormat;
StringmDateFormat;
StringmGeneralFormat;
SharedPtr<System::Collections::Generic::List<SharedPtr<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>>>mFormatInvocations;

SharedPtr<System::Collections::Generic::List<SharedPtr<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>>>get_FormatInvocations()
{
returnmFormatInvocations;
}

StringFormat(SharedPtr<System::Object>value,GeneralFormatformat)
{
if(String::IsNullOrEmpty(mGeneralFormat))
{
returnnullptr;
}

StringnewValue=String::Format(mGeneralFormat,value);
get_FormatInvocations()->Add(MakeObject<ExDocumentBuilder::FieldResultFormatter::FormatInvocation>(
ApiExamples::ExDocumentBuilder::FieldResultFormatter::FormatInvocationType::General,value,System::ObjectExt::ToString(format),newValue));
returnnewValue;
}
