builder->get_Font()->set_NameBi(u"Andalus");
builder->get_Font()->set_SizeBi(24);
builder->get_Font()->set_ItalicBi(true);
builder->get_Font()->set_BoldBi(true);
builder->get_Font()->set_LocaleIdBi(MakeObject<System::Globalization::CultureInfo>(u"ar-AR",false)->get_LCID());

//WecanusetheBidiflagtoindicatewhetherthetextweareabouttoadd
//withthedocumentbuilderisright-to-left.Whenweaddtextwiththisflagsettotrue,
//itwillbeformattedusingtheright-to-leftsetoffontsettings.
builder->get_Font()->set_Bidi(true);
builder->Write(u"");

//Settheflagtofalse,andthenaddleft-to-righttext.
//Thedocumentbuilderwillformattheseusingtheleft-to-rightsetoffontsettings.
builder->get_Font()->set_Bidi(false);
builder->Write(u"Helloworld!");

doc->Save(ArtifactsDir+u"Font.Bidi.docx");

