detaileddescription.para:
document



A document whose page layout model to enumerate.



Examples


Shows ways of traversing a document's layout entities. voidLayoutEnumerator_()
{
//Openadocumentthatcontainsavarietyoflayoutentities.
//Layoutentitiesarepages,cells,rows,lines,andotherobjectsincludedintheLayoutEntityTypeenum.
//Eachlayoutentityhasarectangularspacethatitoccupiesinthedocumentbody.
autodoc=MakeObject<Document>(MyDir+u"Layoutentities.docx");

//Createanenumeratorthatcantraversetheseentitieslikeatree.
autolayoutEnumerator=MakeObject<LayoutEnumerator>(doc);

ASPOSE_ASSERT_EQ(doc,layoutEnumerator->get_Document());

layoutEnumerator->MoveParent(LayoutEntityType::Page);

ASSERT_EQ(LayoutEntityType::Page,layoutEnumerator->get_Type());
ASSERT_THROW(layoutEnumerator->get_Text(),System::InvalidOperationException);

//Wecancallthismethodtomakesurethattheenumeratorwillbeatthefirstlayoutentity.
layoutEnumerator->Reset();

//Therearetwoordersthatdeterminehowthelayoutenumeratorcontinuestraversinglayoutentities
//whenitencountersentitiesthatspanacrossmultiplepages.
//1-Invisualorder:
//Whenmovingthroughanentity'schildrenthatspanmultiplepages,
//pagelayouttakesprecedence,andwemovetootherchildelementsonthispageandavoidtheonesonthenext.
std::cout<<"Traversingfromfirsttolast,elementsbetweenpagesseparated:"<<std::endl;
TraverseLayoutForward(layoutEnumerator,1);

//Ourenumeratorisnowattheendofthecollection.Wecantraversethelayoutentitiesbackwardstogobacktothebeginning.
std::cout<<"Traversingfromlasttofirst,elementsbetweenpagesseparated:"<<std::endl;
TraverseLayoutBackward(layoutEnumerator,1);

//2-Inlogicalorder:
//Whenmovingthroughanentity'schildrenthatspanmultiplepages,
//theenumeratorwillmovebetweenpagestotraverseallthechildentities.
std::cout<<"Traversingfromfirsttolast,elementsbetweenpagesmixed:"<<std::endl;
TraverseLayoutForwardLogical(layoutEnumerator,1);

std::cout<<"Traversingfromlasttofirst,elementsbetweenpagesmixed:"<<std::endl;
TraverseLayoutBackwardLogical(layoutEnumerator,1);
}

staticvoidTraverseLayoutForward(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveFirstChild())
{
TraverseLayoutForward(layoutEnumerator,depth+1);
layoutEnumerator->MoveParent();
}
}while(layoutEnumerator->MoveNext());
}

staticvoidTraverseLayoutBackward(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveLastChild())
{
TraverseLayoutBackward(layoutEnumerator,depth+1);
layoutEnumerator->MoveParent();
}
}while(layoutEnumerator->MovePrevious());
}

staticvoidTraverseLayoutForwardLogical(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveFirstChild())
{
TraverseLayoutForwardLogical(layoutEnumerator,depth+1);
layoutEnumerator->MoveParent();
}
}while(layoutEnumerator->MoveNextLogical());
}

staticvoidTraverseLayoutBackwardLogical(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveLastChild())
{
TraverseLayoutBackwardLogical(layoutEnumerator,depth+1);
layoutEnumerator->MoveParent();
}
}while(layoutEnumerator->MovePreviousLogical());
}

staticvoidPrintCurrentEntity(SharedPtr<LayoutEnumerator>layoutEnumerator,intindent)
{
Stringtabs(u'\t',indent);

std::cout<<(layoutEnumerator->get_Kind()==String::Empty
?String::Format(u"{0}->Entitytype:{1}",tabs,layoutEnumerator->get_Type())
:String::Format(u"{0}->Entitytype&kind:{1},{2}",tabs,layoutEnumerator->get_Type(),layoutEnumerator->get_Kind()))
<<std::endl;

//Onlyspanscancontaintext.
if(layoutEnumerator->get_Type()==LayoutEntityType::Span)
{
std::cout<<tabs<<"Spancontents:\""<<layoutEnumerator->get_Text()<<"\""<<std::endl;
}

System::Drawing::RectangleFleRect=layoutEnumerator->get_Rectangle();
std::cout<<tabs<<"Rectangledimensions"<<leRect.get_Width()<<"x"<<leRect.get_Height()<<",X="<<leRect.get_X()
<<"Y="<<leRect.get_Y()<<std::endl;
std::cout<<tabs<<"Page"<<layoutEnumerator->get_PageIndex()<<std::endl;
}

Examples


Shows how to see the the ranges of pages that a node spans. autodoc=MakeObject<Document>();
autolayoutCollector=MakeObject<LayoutCollector>(doc);

//Callthe"GetNumPagesSpanned"methodtocounthowmanypagesthecontentofourdocumentspans.
//Sincethedocumentisempty,thatnumberofpagesiscurrentlyzero.
ASPOSE_ASSERT_EQ(doc,layoutCollector->get_Document());
ASSERT_EQ(0,layoutCollector->GetNumPagesSpanned(doc));

//Populatethedocumentwith5pagesofcontent.
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Write(u"Section1");
builder->InsertBreak(BreakType::PageBreak);
builder->InsertBreak(BreakType::PageBreak);
builder->InsertBreak(BreakType::SectionBreakEvenPage);
builder->Write(u"Section2");
builder->InsertBreak(BreakType::PageBreak);
builder->InsertBreak(BreakType::PageBreak);

//Beforethelayoutcollector,weneedtocallthe"UpdatePageLayout"methodtogiveus
//anaccuratefigureforanylayout-relatedmetric,suchasthepagecount.
ASSERT_EQ(0,layoutCollector->GetNumPagesSpanned(doc));

layoutCollector->Clear();
doc->UpdatePageLayout();

ASSERT_EQ(5,layoutCollector->GetNumPagesSpanned(doc));

//Wecanseethenumbersofthestartandendpagesofanynodeandtheiroverallpagespans.
SharedPtr<NodeCollection>nodes=doc->GetChildNodes(NodeType::Any,true);
for(constauto&node:System::IterateOver(nodes))
{
std::cout<<String::Format(u"->NodeType.{0}:",node->get_NodeType())<<std::endl;
std::cout<<(String::Format(u"\tStartsonpage{0},endsonpage{1},",layoutCollector->GetStartPageIndex(node),
layoutCollector->GetEndPageIndex(node))+
String::Format(u"spanning{0}pages.",layoutCollector->GetNumPagesSpanned(node)))
<<std::endl;
}

//WecaniterateoverthelayoutentitiesusingaLayoutEnumerator.
autolayoutEnumerator=MakeObject<LayoutEnumerator>(doc);

ASSERT_EQ(LayoutEntityType::Page,layoutEnumerator->get_Type());

//TheLayoutEnumeratorcantraversethecollectionoflayoutentitieslikeatree.
//Wecanalsoapplyittoanynode'scorrespondinglayoutentity.
layoutEnumerator->set_Current(layoutCollector->GetEntity(doc->GetChild(NodeType::Paragraph,1,true)));

ASSERT_EQ(LayoutEntityType::Span,layoutEnumerator->get_Type());
ASSERT_EQ(u"Â¶",layoutEnumerator->get_Text());

