std::cout<<"Traversingfromfirsttolast,elementsbetweenpagesseparated:"<<std::endl;
TraverseLayoutForward(layoutEnumerator,1);

//Ourenumeratorisnowattheendofthecollection.Wecantraversethelayoutentitiesbackwardstogobacktothebeginning.
std::cout<<"Traversingfromlasttofirst,elementsbetweenpagesseparated:"<<std::endl;
TraverseLayoutBackward(layoutEnumerator,1);

//2-Inlogicalorder:
//Whenmovingthroughanentity'schildrenthatspanmultiplepages,
//theenumeratorwillmovebetweenpagestotraverseallthechildentities.
std::cout<<"Traversingfromfirsttolast,elementsbetweenpagesmixed:"<<std::endl;
TraverseLayoutForwardLogical(layoutEnumerator,1);

std::cout<<"Traversingfromlasttofirst,elementsbetweenpagesmixed:"<<std::endl;
TraverseLayoutBackwardLogical(layoutEnumerator,1);
}

staticvoidTraverseLayoutForward(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveFirstChild())
{
TraverseLayoutForward(layoutEnumerator,depth+1);
layoutEnumerator->MoveParent();
}
}while(layoutEnumerator->MoveNext());
}

staticvoidTraverseLayoutBackward(SharedPtr<LayoutEnumerator>layoutEnumerator,intdepth)
{
do
{
PrintCurrentEntity(layoutEnumerator,depth);

if(layoutEnumerator->MoveLastChild())
{
TraverseLayoutBackward(layoutEnumerator,depth+1);
