autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertarevision,thenchangethecolorofallrevisionstogreen.
builder->Writeln(u"Thisisnotarevision.");
doc->StartTrackRevisions(u"JohnDoe",System::DateTime::get_Now());
builder->Writeln(u"Thisisarevision.");
doc->StopTrackRevisions();
builder->Writeln(u"Thisisnotarevision.");

//Removethebarthatappearstotheleftofeveryrevisedline.
doc->get_LayoutOptions()->get_RevisionOptions()->set_InsertedTextColor(RevisionColor::BrightGreen);
doc->get_LayoutOptions()->get_RevisionOptions()->set_ShowRevisionBars(false);

doc->Save(ArtifactsDir+u"Document.LayoutOptionsRevisions.pdf");

