detaileddescription.para:
Examples


Shows how to execute a mail merge with a custom callback that handles merge data in the form of HTML documents. voidMergeHtml()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->InsertField(u"MERGEFIELDhtml_Title\\bContent");
builder->InsertField(u"MERGEFIELDhtml_Body\\bContent");

ArrayPtr<SharedPtr<System::Object>>mergeData=MakeArray<SharedPtr<System::Object>>(
{System::ObjectExt::Box<String>(String(u"<html>")+u"<h1>"+u"<spanstyle=\"color:#0000ff;font-family:Arial;\">HelloWorld!</span>"+
u"</h1>"+u"</html>"),
System::ObjectExt::Box<String>(
String(u"<html>")+u"<blockquote>"+
u"<p>Loremipsumdolorsitamet,consecteturadipiscingelit,seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.</p>"+
u"</blockquote>"+u"</html>")});

doc->get_MailMerge()->set_FieldMergingCallback(MakeObject<ExMailMergeEvent::HandleMergeFieldInsertHtml>());
doc->get_MailMerge()->Execute(MakeArray<String>({u"html_Title",u"html_Body"}),mergeData);

doc->Save(ArtifactsDir+u"MailMergeEvent.MergeHtml.docx");
}

classHandleMergeFieldInsertHtml:publicIFieldMergingCallback
{
private:
voidFieldMerging(SharedPtr<FieldMergingArgs>args)override
{
if(args->get_DocumentFieldName().StartsWith(u"html_")&&args->get_Field()->GetFieldCode().Contains(u"\\b"))
{
//AddparsedHTMLdatatothedocument'sbody.
autobuilder=MakeObject<DocumentBuilder>(args->get_Document());
builder->MoveToMergeField(args->get_DocumentFieldName());
builder->InsertHtml(System::ObjectExt::Unbox<String>(args->get_FieldValue()));

//Sincewehavealreadyinsertedthemergedcontentmanually,
//wewillnotneedtorespondtothiseventbyreturningcontentviathe"Text"property.
args->set_Text(String::Empty);
}
}

voidImageFieldMerging(SharedPtr<ImageFieldMergingArgs>args)override
{
//Donothing.
}
};

