Shows how to use mail merge regions to execute a nested mail merge. voidCustomDataSource()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Normally,MERGEFIELDscontainthenameofacolumnofamailmergedatasource.
//Instead,wecanuse"TableStart:"and"TableEnd:"prefixestobegin/endamailmergeregion.
//Eachregionwillbelongtoatablewithanamethatmatchesthestringimmediatelyaftertheprefix'scolon.
builder->InsertField(u"MERGEFIELDTableStart:Customers");

//TheseMERGEFIELDsareinsidethemailmergeregionofthe"Customers"table.
//Whenweexecutethemailmerge,thisfieldwillreceivedatafromrowsinadatasourcenamed"Customers".
builder->Write(u"Fullname:\t");
builder->InsertField(u"MERGEFIELDFullName");
builder->Write(u"\nAddress:\t");
builder->InsertField(u"MERGEFIELDAddress");
builder->Write(u"\nOrders:\n");

//Createasecondmailmergeregioninsidetheouterregionforadatasourcenamed"Orders".
//The"Orders"dataentrieshaveamany-to-onerelationshipwiththe"Customers"datasource.
builder->InsertField(u"MERGEFIELDTableStart:Orders");

builder->Write(u"\tItemname:\t");
builder->InsertField(u"MERGEFIELDName");
builder->Write(u"\n\tQuantity:\t");
builder->InsertField(u"MERGEFIELDQuantity");
builder->InsertParagraph();

builder->InsertField(u"MERGEFIELDTableEnd:Orders");
builder->InsertField(u"MERGEFIELDTableEnd:Customers");

//Createrelateddatawithnamesthatmatchthoseofourmailmergeregions.
SharedPtr<System::Collections::Generic::List<SharedPtr<ExMailMergeCustomNested::Customer>>>customers=
MakeObject<System::Collections::Generic::List<SharedPtr<ExMailMergeCustomNested::Customer>>>();
customers->Add(MakeObject<ExMailMergeCustomNested::Customer>(u"ThomasHardy",u"120HanoverSq.,London"));
customers->Add(MakeObject<ExMailMergeCustomNested::Customer>(u"PaoloAccorti",u"ViaMonteBianco34,Torino"));

customers->idx_get(0)->get_Orders()->Add(MakeObject<ExMailMergeCustomNested::Order>(u"RugbyWorldCupCap",2));
customers->idx_get(0)->get_Orders()->Add(MakeObject<ExMailMergeCustomNested::Order>(u"RugbyWorldCupBall",1));
customers->idx_get(1)->get_Orders()->Add(MakeObject<ExMailMergeCustomNested::Order>(u"RugbyWorldCupGuide",1));

//Tomailmergefromyourdatasource,wemustwrapitintoanobjectthatimplementstheIMailMergeDataSourceinterface.
autocustomersDataSource=MakeObject<ExMailMergeCustomNested::CustomerMailMergeDataSource>(customers);

doc->get_MailMerge()->ExecuteWithRegions(customersDataSource);

doc->Save(ArtifactsDir+u"NestedMailMergeCustom.CustomDataSource.docx");
}

