dataSourceRoot



An object that implements the custom mail merge data source root interface.



Examples


Performs mail merge from a custom data source with master-detail data. voidCustomDataSourceRoot()
{
//Createadocumentwithtwomailmergeregionsnamed"Washington"and"Seattle".
ArrayPtr<String>mailMergeRegions=MakeArray<String>({u"Vancouver",u"Seattle"});
SharedPtr<Document>doc=CreateSourceDocumentWithMailMergeRegions(mailMergeRegions);

//Createtwodatasourcesforthemailmerge.
SharedPtr<System::Collections::Generic::List<SharedPtr<ExMailMergeCustom::Employee>>>employeesWashingtonBranch=
MakeObject<System::Collections::Generic::List<SharedPtr<ExMailMergeCustom::Employee>>>();
employeesWashingtonBranch->Add(MakeObject<ExMailMergeCustom::Employee>(u"JohnDoe",u"Sales"));
employeesWashingtonBranch->Add(MakeObject<ExMailMergeCustom::Employee>(u"JaneDoe",u"Management"));

SharedPtr<System::Collections::Generic::List<SharedPtr<ExMailMergeCustom::Employee>>>employeesSeattleBranch=
MakeObject<System::Collections::Generic::List<SharedPtr<ExMailMergeCustom::Employee>>>();
employeesSeattleBranch->Add(MakeObject<ExMailMergeCustom::Employee>(u"JohnCardholder",u"Management"));
employeesSeattleBranch->Add(MakeObject<ExMailMergeCustom::Employee>(u"JoeBloggs",u"Sales"));

//Registerourdatasourcesbynameinadatasourceroot.
//Ifweareabouttousethisdatasourcerootinamailmergewithregions,
//eachsource'sregisterednamemustmatchthenameofanexistingmailmergeregioninthemailmergesourcedocument.
autosourceRoot=MakeObject<ExMailMergeCustom::DataSourceRoot>();
sourceRoot->RegisterSource(mailMergeRegions[0],MakeObject<ExMailMergeCustom::EmployeeListMailMergeSource>(employeesWashingtonBranch));
sourceRoot->RegisterSource(mailMergeRegions[1],MakeObject<ExMailMergeCustom::EmployeeListMailMergeSource>(employeesSeattleBranch));

//Sincewehaveconsecutivemailmergeregions,wewouldnormallyhavetoperformtwomailmerges.
//However,onemailmergesourcewithadatarootcanfillinmultipleregions
//iftherootcontainstableswithcorrespondingnames/columnnames.
doc->get_MailMerge()->ExecuteWithRegions(sourceRoot);

doc->Save(ArtifactsDir+u"MailMergeCustom.CustomDataSourceRoot.docx");
}

staticSharedPtr<Document>CreateSourceDocumentWithMailMergeRegions(ArrayPtr<String>regions)
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

for(Strings:regions)
{
builder->Writeln(String(u"\n")+s+u"branch:");
builder->InsertField(String(u"MERGEFIELDTableStart:")+s);
builder->InsertField(u"MERGEFIELDFullName");
builder->Write(u",");
builder->InsertField(u"MERGEFIELDDepartment");
builder->InsertField(String(u"MERGEFIELDTableEnd:")+s);
}

returndoc;
}

