Shows how to remove paragraphs with punctuation marks after a mail merge operation. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

automergeFieldOption1=System::DynamicCast<FieldMergeField>(builder->InsertField(u"MERGEFIELD",u"Option_1"));
mergeFieldOption1->set_FieldName(u"Option_1");

builder->Write(punctuationMark);

automergeFieldOption2=System::DynamicCast<FieldMergeField>(builder->InsertField(u"MERGEFIELD",u"Option_2"));
mergeFieldOption2->set_FieldName(u"Option_2");

//Configurethe"CleanupOptions"propertytoremoveanyemptyparagraphsthatthismailmergewouldcreate.
doc->get_MailMerge()->set_CleanupOptions(MailMergeCleanupOptions::RemoveEmptyParagraphs);

//Settingthe"CleanupParagraphsWithPunctuationMarks"propertyto"true"willalsocountparagraphs
//withpunctuationmarksasemptyandwillgetthemailmergeoperationtoremovethemaswell.
//Settingthe"CleanupParagraphsWithPunctuationMarks"propertyto"false"
//willremoveemptyparagraphs,butnotoneswithpunctuationmarks.
//Thisisalistofpunctuationmarksthatthispropertyconcerns:"!",",",".",":",";","?","¡","¿".
doc->get_MailMerge()->set_CleanupParagraphsWithPunctuationMarks(cleanupParagraphsWithPunctuationMarks);

doc->get_MailMerge()->Execute(MakeArray<String>({u"Option_1",u"Option_2"}),MakeArray<SharedPtr<System::Object>>({nullptr,nullptr}));

doc->Save(ArtifactsDir+u"MailMerge.RemoveColonBetweenEmptyMergeFields.docx");

