builder->InsertField(u"MERGEFIELDmyMergeField",nullptr);

doc->get_MailMerge()->set_TrimWhitespaces(trimWhitespaces);
doc->get_MailMerge()->Execute(MakeArray<String>({u"myMergeField"}),
MakeArray<SharedPtr<System::Object>>({System::ObjectExt::Box<String>(u"\thelloworld!")}));

ASSERT_EQ(trimWhitespaces?String(u"helloworld!\f"):String(u"\thelloworld!\f"),doc->GetText());

regionName



Region name (case-insensitive).



Examples


Shows how to create, list, and read mail merge regions. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//"TableStart"and"TableEnd"tags,whichgoinsideMERGEFIELDs,
//denotethestringsthatsignifythestartsandendsofmailmergeregions.
ASSERT_EQ(u"TableStart",doc->get_MailMerge()->get_RegionStartTag());
ASSERT_EQ(u"TableEnd",doc->get_MailMerge()->get_RegionEndTag());

//Usethesetagstostartandendamailmergeregionnamed"MailMergeRegion1",
//whichwillcontainMERGEFIELDsfortwocolumns.
builder->InsertField(u"MERGEFIELDTableStart:MailMergeRegion1");
builder->InsertField(u"MERGEFIELDColumn1");
builder->Write(u",");
builder->InsertField(u"MERGEFIELDColumn2");
builder->InsertField(u"MERGEFIELDTableEnd:MailMergeRegion1");

//Wecankeeptrackofmergeregionsandtheircolumnsbylookingatthesecollections.
SharedPtr<System::Collections::Generic::IList<SharedPtr<MailMergeRegionInfo>>>regions=doc->get_MailMerge()->GetRegionsByName(u"MailMergeRegion1");

ASSERT_EQ(1,regions->get_Count());
ASSERT_EQ(u"MailMergeRegion1",regions->idx_get(0)->get_Name());

ArrayPtr<String>mergeFieldNames=doc->get_MailMerge()->GetFieldNamesForRegion(u"MailMergeRegion1");

ASSERT_EQ(u"Column1",mergeFieldNames[0]);
ASSERT_EQ(u"Column2",mergeFieldNames[1]);

//Insertaregionwiththesamenameinsidetheexistingregion,whichwillmakeitaparent.
//Nowa"Column2"fieldwillbeinsideanewregion.
builder->MoveToField(regions->idx_get(0)->get_Fields()->idx_get(1),false);
builder->InsertField(u"MERGEFIELDTableStart:MailMergeRegion1");
builder->MoveToField(regions->idx_get(0)->get_Fields()->idx_get(1),true);
builder->InsertField(u"MERGEFIELDTableEnd:MailMergeRegion1");

//Ifwelookupthenameofduplicateregionsusingthe"GetRegionsByName"method,
//itwillreturnallsuchregionsinacollection.
regions=doc->get_MailMerge()->GetRegionsByName(u"MailMergeRegion1");

ASSERT_EQ(2,regions->get_Count());
//Checkthatthesecondregionnowhasaparentregion.
ASSERT_EQ(u"MailMergeRegion1",regions->idx_get(1)->get_ParentRegion()->get_Name());

mergeFieldNames=doc->get_MailMerge()->GetFieldNamesForRegion(u"MailMergeRegion1",1);

ASSERT_EQ(u"Column2",mergeFieldNames[0]);

