Examples


Shows how the checksum is calculated in a runtime. autodoc=MakeObject<Document>();

autorichText=MakeObject<StructuredDocumentTag>(doc,SdtType::RichText,MarkupLevel::Block);
doc->get_FirstSection()->get_Body()->AppendChild(richText);

//Thechecksumisread-onlyandcomputedusingthedataofthecorrespondingcustomXMLdatapart.
richText->get_XmlMapping()->SetMapping(
doc->get_CustomXmlParts()->Add(System::ObjectExt::ToString(System::Guid::NewGuid()),u"<root><text>ContentControl</text></root>"),u"/root/text",
u"");

int64_tchecksum=richText->get_XmlMapping()->get_CustomXmlPart()->get_DataChecksum();
std::cout<<checksum<<std::endl;

richText->get_XmlMapping()->SetMapping(
doc->get_CustomXmlParts()->Add(System::ObjectExt::ToString(System::Guid::NewGuid()),u"<root><text>UpdatedContentControl</text></root>"),
u"/root/text",u"");

int64_tupdatedChecksum=richText->get_XmlMapping()->get_CustomXmlPart()->get_DataChecksum();
std::cout<<updatedChecksum<<std::endl;

//WechangedtheXmlPartofthetag,andthechecksumwasupdatedatruntime.
ASSERT_NE(checksum,updatedChecksum);

