Examples


Shows how to set XML mappings for custom XML parts. autodoc=MakeObject<Document>();

//ConstructanXMLpartthatcontainstextandaddittothedocument'sCustomXmlPartcollection.
StringxmlPartId=System::Guid::NewGuid().ToString(u"B");
StringxmlPartContent=u"<root><text>Textelement#1</text><text>Textelement#2</text></root>";
SharedPtr<CustomXmlPart>xmlPart=doc->get_CustomXmlParts()->Add(xmlPartId,xmlPartContent);

ASSERT_EQ(u"<root><text>Textelement#1</text><text>Textelement#2</text></root>",System::Text::Encoding::get_UTF8()->GetString(xmlPart->get_Data()));

//CreateastructureddocumenttagthatwilldisplaythecontentsofourCustomXmlPart.
autotag=MakeObject<StructuredDocumentTag>(doc,SdtType::PlainText,MarkupLevel::Block);

//Setamappingforourstructureddocumenttag.Thismappingwillinstruct
//ourstructureddocumenttagtodisplayaportionoftheXMLpart'stextcontentsthattheXPathpointsto.
//Inthiscase,itwillbecontentsofthethesecond"<text>"elementofthefirst"<root>"element:"Textelement#2".
tag->get_XmlMapping()->SetMapping(xmlPart,u"/root[1]/text[2]",u"xmlns:ns='http://www.w3.org/2001/XMLSchema'");

ASSERT_TRUE(tag->get_XmlMapping()->get_IsMapped());
ASPOSE_ASSERT_EQ(xmlPart,tag->get_XmlMapping()->get_CustomXmlPart());
ASSERT_EQ(u"/root[1]/text[2]",tag->get_XmlMapping()->get_XPath());
ASSERT_EQ(u"xmlns:ns='http://www.w3.org/2001/XMLSchema'",tag->get_XmlMapping()->get_PrefixMappings());

//Addthestructureddocumenttagtothedocumenttodisplaythecontentfromourcustompart.
doc->get_FirstSection()->get_Body()->AppendChild(tag);
doc->Save(ArtifactsDir+u"StructuredDocumentTag.XmlMapping.docx");

