{
std::cout<<"Listitemparagraph#"<<paras->IndexOf(paragraph)<<std::endl;

//Thisisthetextwegetwhengettingwhenweoutputthisnodetotextformat.
//Thistextoutputwillomitlistlabels.Trimanyparagraphformattingcharacters.
StringparagraphText=paragraph->ToString(SaveFormat::Text).Trim();
std::cout<<"\tExportedText:"<<paragraphText<<std::endl;

SharedPtr<ListLabel>label=paragraph->get_ListLabel();

//Thisgetsthepositionoftheparagraphinthecurrentlevelofthelist.Ifwehavealistwithmultiplelevels,
//thiswilltelluswhatpositionitisonthatlevel.
std::cout<<"\tNumericalId:"<<label->get_LabelValue()<<std::endl;

//Combinethemtogethertoincludethelistlabelwiththetextintheoutput.
std::cout<<"\tListlabelcombinedwithtext:"<<label->get_LabelString()<<""<<paragraphText<<std::endl;
}

Exports the content of a node to String in HTML format. autodoc=MakeObject<Document>(MyDir+u"Document.docx");

SharedPtr<Node>node=doc->get_LastSection()->get_Body()->get_LastParagraph();

//WhenwecalltheToStringmethodusingthehtmlSaveFormatoverload,
//itconvertsthenode'scontentstotheirrawhtmlrepresentation.
ASSERT_EQ(String(u"<pstyle=\"margin-top:0pt;margin-bottom:8pt;line-height:108%;font-size:12pt\">")+
u"<spanstyle=\"font-family:'TimesNewRoman'\">HelloWorld!</span>"+u"</p>",
node->ToString(SaveFormat::Html));

//WecanalsomodifytheresultofthisconversionusingaSaveOptionsobject.
autosaveOptions=MakeObject<HtmlSaveOptions>();
saveOptions->set_ExportRelativeFontSize(true);

ASSERT_EQ(String(u"<pstyle=\"margin-top:0pt;margin-bottom:8pt;line-height:108%\">")+
u"<spanstyle=\"font-family:'TimesNewRoman'\">HelloWorld!</span>"+u"</p>",
node->ToString(saveOptions));

saveOptions



Specifies the options that control how the node is saved.




The content of the node in the specified format.

