Shows how to clone a composite node. autodoc=MakeObject<Document>();
SharedPtr<Paragraph>para=doc->get_FirstSection()->get_Body()->get_FirstParagraph();
para->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

//Belowaretwowaysofcloningacompositenode.
//1-Createacloneofanode,andcreateacloneofeachofitschildnodesaswell.
SharedPtr<Node>cloneWithChildren=para->Clone(true);

ASSERT_TRUE((System::DynamicCast<CompositeNode>(cloneWithChildren))->get_HasChildNodes());
ASSERT_EQ(u"Helloworld!",cloneWithChildren->GetText().Trim());

//2-Createacloneofanodejustbyitselfwithoutanychildren.
SharedPtr<Node>cloneWithoutChildren=para->Clone(false);

ASSERT_FALSE((System::DynamicCast<CompositeNode>(cloneWithoutChildren))->get_HasChildNodes());
ASSERT_EQ(String::Empty,cloneWithoutChildren->GetText().Trim());

Shows how to traverse through a composite node's collection of child nodes. autodoc=MakeObject<Document>();

//Addtworunsandoneshapeaschildnodestothefirstparagraphofthisdocument.
autoparagraph=System::DynamicCast<Paragraph>(doc->GetChild(NodeType::Paragraph,0,true));
paragraph->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

autoshape=MakeObject<Shape>(doc,ShapeType::Rectangle);
shape->set_Width(200);
shape->set_Height(200);
//Notethatthe'CustomNodeId'isnotsavedtoanoutputfileandexistsonlyduringthenodelifetime.
shape->set_CustomNodeId(100);
shape->set_WrapType(WrapType::Inline);
paragraph->AppendChild(shape);

paragraph->AppendChild(MakeObject<Run>(doc,u"Helloagain!"));

//Iteratethroughtheparagraph'scollectionofimmediatechildren,
//andprintanyrunsorshapesthatwefindwithin.
SharedPtr<NodeCollection>children=paragraph->get_ChildNodes();

ASSERT_EQ(3,paragraph->get_ChildNodes()->get_Count());

for(constauto&child:System::IterateOver(children))
{
switch(child->get_NodeType())
{
caseNodeType::Run:
std::cout<<"Runcontents:"<<std::endl;
std::cout<<"\t\""<<child->GetText().Trim()<<"\""<<std::endl;
break;

caseNodeType::Shape:{
autochildShape=System::DynamicCast<Shape>(child);
std::cout<<"Shape:"<<std::endl;
std::cout<<String::Format(u"\t{0},{1}x{2}",childShape->get_ShapeType(),childShape->get_Width(),childShape->get_Height())<<std::endl;
ASSERT_EQ(100,shape->get_CustomNodeId());
break;
}

default:
break;
}
}

Shows how to remove all child nodes of a specific type from a composite node. autodoc=MakeObject<Document>(MyDir+u"Tables.docx");

ASSERT_EQ(2,doc->GetChildNodes(NodeType::Table,true)->get_Count());

SharedPtr<Node>curNode=doc->get_FirstSection()->get_Body()->get_FirstChild();

while(curNode!=nullptr)
{
//Savethenextsiblingnodeasavariableincasewewanttomovetoitafterdeletingthisnode.
SharedPtr<Node>nextNode=curNode->get_NextSibling();

//AsectionbodycancontainParagraphandTablenodes.
//IfthenodeisaTable,removeitfromtheparent.
if(curNode->get_NodeType()==NodeType::Table)
{
curNode->Remove();
}

curNode=nextNode;
}

ASSERT_EQ(0,doc->GetChildNodes(NodeType::Table,true)->get_Count());
