Shows how to set the order of text columns in a section. autodoc=MakeObject<Document>();

SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(0)->get_PageSetup();
pageSetup->get_TextColumns()->SetCount(3);

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Write(u"Column1.");
builder->InsertBreak(BreakType::ColumnBreak);
builder->Write(u"Column2.");
builder->InsertBreak(BreakType::ColumnBreak);
builder->Write(u"Column3.");

//Setthe"Bidi"propertyto"true"toarrangethecolumnsstartingfromthepage'srightside.
//Theorderofthecolumnswillmatchthedirectionoftheright-to-lefttext.
//Setthe"Bidi"propertyto"false"toarrangethecolumnsstartingfromthepage'sleftside.
//Theorderofthecolumnswillmatchthedirectionoftheleft-to-righttext.
pageSetup->set_Bidi(reverseColumns);

doc->Save(ArtifactsDir+u"PageSetup.Bidi.docx");

