Shows how to apply a border to the page and header/footer. autodoc=MakeObject<Document>();

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"Helloworld!Thisisthemainbodytext.");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Write(u"Thisistheheader.");
builder->MoveToHeaderFooter(HeaderFooterType::FooterPrimary);
builder->Write(u"Thisisthefooter.");
builder->MoveToDocumentEnd();

//Insertabluedouble-lineborder.
SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(0)->get_PageSetup();
pageSetup->get_Borders()->set_LineStyle(LineStyle::Double);
pageSetup->get_Borders()->set_Color(System::Drawing::Color::get_Blue());

//Asection'sPageSetupobjecthas"BorderSurroundsHeader"and"BorderSurroundsFooter"flagsthatdetermine
//whetherapagebordersurroundsthemainbodytext,alsoincludestheheaderorfooter,respectively.
//Setthe"BorderSurroundsHeader"flagto"true"tosurroundtheheaderwithourborder,
//andthensetthe"BorderSurroundsFooter"flagtoleavethefooteroutsideoftheborder.
pageSetup->set_BorderSurroundsHeader(true);
pageSetup->set_BorderSurroundsFooter(false);

doc->Save(ArtifactsDir+u"PageSetup.PageBorder.docx");

