Shows how to create headers and footers in a document using DocumentBuilder. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Specifythatwewantdifferentheadersandfootersforfirst,evenandoddpages.
builder->get_PageSetup()->set_DifferentFirstPageHeaderFooter(true);
builder->get_PageSetup()->set_OddAndEvenPagesHeaderFooter(true);

//Createtheheaders,thenaddthreepagestothedocumenttodisplayeachheadertype.
builder->MoveToHeaderFooter(HeaderFooterType::HeaderFirst);
builder->Write(u"Headerforthefirstpage");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderEven);
builder->Write(u"Headerforevenpages");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Write(u"Headerforallotherpages");

builder->MoveToSection(0);
builder->Writeln(u"Page1");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page2");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page3");

doc->Save(ArtifactsDir+u"DocumentBuilder.HeadersAndFooters.docx");

Shows how to track the order in which a text replacement operation traverses nodes. voidOrder(booldifferentFirstPageHeaderFooter)
{
autodoc=MakeObject<Document>(MyDir+u"Headerandfootertypes.docx");

SharedPtr<Section>firstPageSection=doc->get_FirstSection();

autologger=MakeObject<ExHeaderFooter::ReplaceLog>();
autooptions=MakeObject<FindReplaceOptions>();
options->set_ReplacingCallback(logger);

//Usingadifferentheader/footerforthefirstpagewillaffectthesearchorder.
firstPageSection->get_PageSetup()->set_DifferentFirstPageHeaderFooter(differentFirstPageHeaderFooter);
doc->get_Range()->Replace(MakeObject<System::Text::RegularExpressions::Regex>(u"(header|footer)"),u"",options);

if(differentFirstPageHeaderFooter)
{
ASSERT_EQ(u"Firstheader\nFirstfooter\nSecondheader\nSecondfooter\nThirdheader\nThirdfooter\n",logger->get_Text().Replace(u"\r",u""));
}
else
{
ASSERT_EQ(u"Thirdheader\nFirstheader\nThirdfooter\nFirstfooter\nSecondheader\nSecondfooter\n",logger->get_Text().Replace(u"\r",u""));
}
}

classReplaceLog:publicIReplacingCallback
{
public:
Stringget_Text()
{
returnmTextBuilder->ToString();
}

ReplaceActionReplacing(SharedPtr<ReplacingArgs>args)override
{
mTextBuilder->AppendLine(args->get_MatchNode()->GetText());
returnReplaceAction::Skip;
}

ReplaceLog():mTextBuilder(MakeObject<System::Text::StringBuilder>())
{
}

private:
SharedPtr<System::Text::StringBuilder>mTextBuilder;
};

Shows how to enable or disable primary headers/footers. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Belowaretwotypesofheader/footers.
//1-The"First"header/footer,whichappearsonthefirstpageofthesection.
builder->MoveToHeaderFooter(HeaderFooterType::HeaderFirst);
builder->Writeln(u"Firstpageheader.");

builder->MoveToHeaderFooter(HeaderFooterType::FooterFirst);
builder->Writeln(u"Firstpagefooter.");

//2-The"Primary"header/footer,whichappearsoneverypageinthesection.
//Wecanoverridetheprimaryheader/footerbyafirstandanevenpageheader/footer.
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Writeln(u"Primaryheader.");

builder->MoveToHeaderFooter(HeaderFooterType::FooterPrimary);
builder->Writeln(u"Primaryfooter.");

builder->MoveToSection(0);
builder->Writeln(u"Page1.");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page2.");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page3.");

//Eachsectionhasa"PageSetup"objectthatspecifiespageappearance-relatedproperties
//suchasorientation,size,andborders.
//Setthe"DifferentFirstPageHeaderFooter"propertyto"true"toapplythefirstheader/footertothefirstpage.
//Setthe"DifferentFirstPageHeaderFooter"propertyto"false"
//tomakethefirstpagedisplaytheprimaryheader/footer.
builder->get_PageSetup()->set_DifferentFirstPageHeaderFooter(differentFirstPageHeaderFooter);

doc->Save(ArtifactsDir+u"PageSetup.DifferentFirstPageHeaderFooter.docx");

