Shows how to configure options affecting footnotes/endnotes in a section. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Helloworld!");
builder->InsertFootnote(FootnoteType::Footnote,u"Footnotereferencetext.");

//Configureallfootnotesinthefirstsectiontorestartthenumberingfrom1
//ateachnewpageanddisplaythemselvesdirectlybeneaththetextoneverypage.
SharedPtr<FootnoteOptions>footnoteOptions=doc->get_Sections()->idx_get(0)->get_PageSetup()->get_FootnoteOptions();
footnoteOptions->set_Position(FootnotePosition::BeneathText);
footnoteOptions->set_RestartRule(FootnoteNumberingRule::RestartPage);
footnoteOptions->set_StartNumber(1);

builder->Write(u"Helloagain.");
builder->InsertFootnote(FootnoteType::Footnote,u"Endnotereferencetext.");

//Configureallendnotesinthefirstsectiontomaintainacontinuouscountthroughoutthesection,
//startingfrom1.Also,setthemalltoappearcollectedattheendofthedocument.
SharedPtr<EndnoteOptions>endnoteOptions=doc->get_Sections()->idx_get(0)->get_PageSetup()->get_EndnoteOptions();
endnoteOptions->set_Position(EndnotePosition::EndOfDocument);
endnoteOptions->set_RestartRule(FootnoteNumberingRule::Continuous);
endnoteOptions->set_StartNumber(1);

doc->Save(ArtifactsDir+u"PageSetup.FootnoteOptions.docx");

