Shows how to set gutter margins. autodoc=MakeObject<Document>();

//Inserttextthatspansseveralpages.
autobuilder=MakeObject<DocumentBuilder>(doc);
for(inti=0;i<6;i++)
{
builder->Write(String(u"Loremipsumdolorsitamet,consecteturadipiscingelit,")+
u"seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.");
builder->InsertBreak(BreakType::PageBreak);
}

//Agutteraddswhitespacestoeithertheleftorrightpagemargin,
//whichmakesupforthecenterfoldingofpagesinabookencroachingonthepage'slayout.
SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(0)->get_PageSetup();

//Determinehowmuchspaceourpageshavefortextwithinthemarginsandthenaddanamounttopadamargin.
ASSERT_NEAR(470.30,pageSetup->get_PageWidth()-pageSetup->get_LeftMargin()-pageSetup->get_RightMargin(),0.01);

pageSetup->set_Gutter(100.0);

//Setthe"RtlGutter"propertyto"true"toplacethegutterinamoresuitablepositionforright-to-lefttext.
pageSetup->set_RtlGutter(true);

//Setthe"MultiplePages"propertyto"MultiplePagesType.MirrorMargins"toalternate
//theleft/rightpagesidepositionofmarginseverypage.
pageSetup->set_MultiplePages(MultiplePagesType::MirrorMargins);

doc->Save(ArtifactsDir+u"PageSetup.Gutter.docx");

Shows how to configure a document that can be printed as a book fold. autodoc=MakeObject<Document>();

//Inserttextthatspans16pages.
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"MyBooklet:");

for(inti=0;i<15;i++)
{
builder->InsertBreak(BreakType::PageBreak);
builder->Write(String::Format(u"Bookletface#{0}",i));
}

//Configurethefirstsection's"PageSetup"propertytoprintthedocumentintheformofabookfold.
//Whenweprintthisdocumentonbothsides,wecantakethepagestostackthem
//andfoldthemalldownthemiddleatonce.Thecontentsofthedocumentwilllineupintoabookfold.
SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(0)->get_PageSetup();
pageSetup->set_MultiplePages(MultiplePagesType::BookFoldPrinting);

//Wecanonlyspecifythenumberofsheetsinmultiplesof4.
pageSetup->set_SheetsPerBooklet(4);

doc->Save(ArtifactsDir+u"PageSetup.Booklet.docx");

