//Wecanchangethecurrentpage'ssizetoapre-definedsize
//byusingthe"PaperSize"propertyofthissection'sPageSetupobject.
builder->get_PageSetup()->set_PaperSize(PaperSize::Tabloid);

ASPOSE_ASSERT_EQ(792.0,builder->get_PageSetup()->get_PageWidth());
ASPOSE_ASSERT_EQ(1224.0,builder->get_PageSetup()->get_PageHeight());

builder->Writeln(String::Format(u"Thispageis{0}x{1}.",builder->get_PageSetup()->get_PageWidth(),builder->get_PageSetup()->get_PageHeight()));

